css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org"),
               domain("images.4chan.org"),
               domain("sys.4chan.org"),
               domain("green-oval.net") {

.rules,
.logo+hr,
.logo+hr+div,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
.postarea>form>div[style]>a[target],
.doubledash {display:none !important}

* {
    border: none !important;
    background: none !important;
    color: inherit !important;
}
html {
    color: #555 !important;
    background: #222 !important;
/*#url('http://i51.tinypic.com/30il5ki.png') fixed bottom right no-repeat */
}
#navtop {
    position: absolute;
    top:0;left:0;right:0;
    color: #666 !important;
    background: #111 !important;
    border-bottom: 1px solid #282828 !important;
    text-align: center;
    margin: -4px;
    padding: 3px;
}
#navtop a {
    color: #9f0 !important;
}
hr {
    border-bottom: 1px solid rgba(0,0,0,0.5) !important;
    border-top: 1px solid rgba(255,255,255,0.1) !important;
}
a>img {
    box-shadow: 0 0 6px #000;
    -moz-box-shadow: 0 0 6px #000;
    -webkit-box-shadow: 0 0 6px #000;
}
.logo b {
    color: #fff !important;
}
/*.postarea {
    color: #000 !important;
    position: fixed;
    top:0; right:0;
}*/
.postblock {
    color: #999 !important;
}
/*.postarea form {
    padding-left: 12px;
    opacity: .1;
    width: 0;
}*/
/*.postarea form:hover {
    width: auto;
    opacity: 1;
    background: rgba(0,0,0,0.8) !important;
    border-radius: 0 0 0 32px;
    -moz-border-radius: 0 0 0 32px;
    -webkit-border-radius: 0 0 0 32px;
    box-shadow: 0 0 32px #000;
    -moz-box-shadow: 0 0 32px #000;
    -webkit-box-shadow: 0 0 32px #000;
}*/
/*tr>th>font { 
    position: fixed;
    top:-2px; right:0;
    font-size: 0;
}
tr>th>font:after {
    color: #f00 !important;
    content: 'REPLY';
    font-size: 48px;
}*/



form>table {
    padding: 4px !important;
}
.reply, .replyhl {
    background: rgba(0,0,0,0.2) !important;
    border: 1px solid #161616 !important;
    margin: 8px !important;
    border-radius: 12px;
    -moz-border-radius: 12px;
    -webkit-border-radius: 12px;
    box-shadow: 0 0 6px rgba(0,0,0,0.5);
    -moz-box-shadow: 0 0 6px rgba(0,0,0,0.5);
    -webkit-box-shadow: 0 0 6px rgba(0,0,0,0.5);
}
.replyhl {
    border: 1px solid #9f0 !important;
    border-width: 4px 0 !important;
}
.postername,.commentpostername {
    color: #999 !important;
}
.postertrip {
    color: #644 !important;
}
blockquote {
    color: #fff !important;
}
.quotejs {
    color: #999 !important;
}
.quotelink {
    color: #9f0 !important;
}
.unkfunc {
    color: #6c6 !important;
}

.commentpostername>span[style] {
    color: #f0c !important;
    font-size: 1.1em;
}
a[href="mailto:sage"] {
    text-decoration: none !important;
}
a[href="mailto:sage"]:before {
    color: #f90;
    content: ' SAGE ';
    text-shadow: 0 -1px 1px #f00;
}

/*.filesize span {
    font-size: .01em;
    letter-spacing: -5em;
}
.filesize span:before {
    font-size: 100em;
    letter-spacing: 0;
    content: attr(title);
}*/
span.spoiler {
    color: #aaa !important;
    background: #fff !important;
}
span.spoiler:hover {
    color: #000 !important;
}
br+br+b[style] {
    color: #f00 !important;
}

blockquote {
    font-size: 97% !important;
}

#recaptcha_table * {
margin-left: 0 !important;
padding-left: 0 !important;
}

#recaptcha_table tr:nth-of-type(1) {
height: 57px !important;
}

#recaptcha_response_field {
margin-top: 1px !important
}

}
