css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

#baseDiv {
width: 1000px !important; }
#movie_player {
width: 615px !important; height: 460px !important;
float: left !important; }
#aboutExploreDiv, #dVidsDiv, #otherVidsCell {padding-left: 130px !important; }
#leaderboardAd {display: none !important; }

#commentsDiv, #logoTagDiv, #utilDiv, #gNavDiv, #gNavBottom, #actionsAreaDiv, #ratingAndStatsDiv, #asDiv, #vrParentDiv, #footerDiv, #ticker, #leaderboardAd, #footer, #copyright, #channelBrandCap, #channelVidsBody, #promotedVidsContainer, 
#nav-item-home, #nav-item-videos, #nav-item-channels, #nav-item-community,
#masthead .upload-button, #relatedVidsToggle, #embedDiv, #urlDiv, .wsHeading,   
.nav, #masthead .bar .leftcap, #masthead .bar .rightcap, #active_sharing_div,
#channelBrandDiv, .hpContentBlock, #hpSideContent {

        display: none !important;

    }

#masthead #search-button, #masthead #search-term {

      float: left !important; 

    }

#masthead .search-bar {

    background: white !important; 

    }

#masthead .bar, #masthead .search-bar {
 width: 693px !important;
}

#vidTitle {
font-size: 23px !important;
height: 30px !important; 
margin-bottom: 5px !important; 
margin-top: 0px !important; 
}

#masthead {
width: 1000px !important;
height: 80px; 
left: 0px !important; 
margin-bottom: 0px !important; 
margin-top: 30px !important; 
}

#masthead .user-info {
float: right !important;
}

#relatedVidsBody scroll {
height: 292px !important; 
}

#masthead { height: 80px !important; }

#searchSectionHeader {
width: 550px; 
}

#searchSectionHeader, #mainContent, #hpMainContent {
margin: auto !important; 
}

#mainContent, #hpMainContent { float: none !important; }


}
