css:
@-moz-document url-prefix("https://openclipart.org") {
#main {background: none 50% 0% repeat-y !important;}

#header, #mainnav, #yellow-bar, #forfooter, #links, #main, #fab-banner, 

#footer {margin: 0px !important;width: 100% !important;padding: 0px !important;}

#header {margin-top:48px !important;}

#logged, div#container ul,  #search {
position:fixed !important;
top:0px !important;
margin:auto !important;
z-index:666 !important;
}

#content {margin-top:0 !important;}
.sidebox h3 {text-align: center !important;}
.sidebox {width: 25% !important;}

#logged {
float:right !important;
background-color:transparent!important;
right:0px!important;
margin-top:-0px!important;
z-index:777!important;
}

#search {background-color:#faad3c!important; position:fixed;right:0px!important;top:48px!important;
}

#mainnav li.launchpad {
float:left!important;
}

.flex {
max-width: 100% !important;
  }
}
