css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {



/* Texts */
/* ============== */

li[total-install-count]:after {
font-family: Calibri !important;
font-size: 0.9em !important;
text-shadow: 0px 0px 4px #676767 !important;
color: #616161 !important;
content: " ( Installs: " attr(total-install-count) ", " attr(weekly-install-count) " wk ) " attr(class); }

h2 {
font-size: 150% !important;
color: rgba(0,0,0,0.6) !important;
text-align: center !important;
text-shadow: 0 0 7px rgba(0,0,0,0.7) !important; }



/* Misc. */
/* ============= */

#front-page-newest {
display: none !important; }

#style-list {
-moz-column-count: 1 !important; }



/* Margins & Paddings + Width & Height */
/* ================================================ */

article {
margin-left: 205px !important; }

#left-sidebar {
position: absolute;
left: 2.75%;
top: 14%;
margin: 0 !important; }

#content-wrapper {
margin-top: 15px !important; }

#content-wrapper {
margin-left: 10px !important;
width: 99% !important; }

body {
width: 96% !important;
margin: 10px auto 0 auto !important; }

button {
margin: auto !important; }

#front-page-best {
max-width: 100% !important;
width: 100% !important;
padding: 0px !important; }



/* Glows& Shadows */
/* ========================== */

#left-sidebar, body{
box-shadow: 0 0 10px 1px rgba(0,0,0,0.6);
border-radius: 7px;
padding: 5px !important; }

#install {
box-shadow: 0 0 4px 2px #7CCD7C;
border-radius: 4px; }

header {
margin: 0 auto 0 auto;
box-shadow: 0 0 4px 2px #FFEC8B;
border-radius: 4px;
margin: 5px !important; }

#related, #left-sidebar > nav {
margin: 0 auto 0 auto;
box-shadow: 0 0 4px 2px #D1EEEE;
border-radius: 4px; }

* {
text-shadow: 0 0 1px rgba(0,0,0,0.7) !important; }
}
