css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pinboard.in") {
body {font-family: Arial; color:#292929}

a {color:#292929!important;padding:2px}
a:hover,.user_navbar a:hover,#tag_cloud_header a:hover {background:#292929!important;color:#FFF!important}
.display .when {display:none}

.display {border-bottom:1px dashed #ccc;width:100%; padding:0 0 20px}
.display .bookmark_title {font: bold 16px Arial; padding:3px;margin:0 0 5px;display:inline-block;}
.display a.bookmark_title {color:#292929;}
.display a.unread {background:#fdebbf}
.display a.bookmark_title:hover {color:#ff0099}
.display .description {font:11px/16px Verdana; margin:5px 0; padding:5px; background:#EEE}
.display .tag {font:11px Verdana; display:inline-block;padding:2px; margin:5px 0!important;background:#ecf7fb}
.display .source {font-size:11px;display:inline-block;margin:0 0 5px}
.display .edit_links {font:11px Arial}

.user_navbar a,#tag_cloud_header a {font-size:16px!important;color:#059ece!important;font-weight:bold}

.star {font-size:22px;display:inline-block; font-weight:bold;margin: 0px 10px -25px -20px;}
.star span {visibility:hidden}
.star:before {content: "â™¥"}
.selected_star, .star:hover {color:#cc0066!important}

#bookmarks .private {background:#fff!important}
#bookmarks .private .display:before {content:"PRIVATE: "; font-size:10px;color:#0099cc}

}
