css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("orgmode.org") {
  #table-of-contents { position: static !important; }
  #table-of-contents, #table-of-contents h2 { float: none !important; }
  #table-of-contents div { display: block !important; }
  #table-of-contents div:hover { margin-top: 0 !important; padding: 0 !important; }
}
