@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://my.deviantart.com/devwatch/") {

.subsection, .subsection h3 {
background:#D5E3DF !important;
-moz-border-radius-topright: 1em !important;
-moz-border-radius-topleft: 1em !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
}

.even, .odd {
background: #D5E3DF !important;
}

.button {
background:#B9D0CA !important;
border:none !important;
}

#content {
border:none !important;
-moz-border-radius-topright: 1em !important; -moz-border-radius-topleft: 1em !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
}

#nav-east {
display:none !important;
background:#FFFFFF;
}

.button {
background:#FFFFFF;
}

body {
background:#6A7872 !important;
}

#top h1, .ttr  {
background-color:#6A7872 !important;
}

.dev-thumbnails {
border:none !important;
background:#D5E3DF !important;
margin-top:-10px;
margin-bottom:-20px;
}

.stream {
border:none !important;
background:#D5E3DF !important;
margin-top:-10px;
margin-bottom:-20px;
}

.output-primary, #output .f {
width:100% !important;
}

.tabbar {
background:#D5E3DF;
}

.tabbar strong, .tabbar a {
border:none !important;
background:#D5E3DF !important;
color:#1983CB !important;
font-size:16px !important;
font-weight: bold !important;
}

.tabbar a:hover {
text-decoration:underline !important;
}

.tabbar strong {
color:#000000 !important;
}

.alt-head {
background:#D5E3DF !important;
border:none !important;
-moz-border-radius-topright: 1em !important; -moz-border-radius-topleft: 1em !important;
padding:1em;
}

.section-foot {
background:#D5E3DF !important;
border:none !important;
}

.section-foot a {
background:#D5E3DF !important;
border:none !important;
}

.section-foot a:hover {
background:#FFFFFF !important;
border:none !important;
}

.read {
border:none !important;
background:#D5E3DF !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
}

.tabbar a:hover, .tabbar a:active {
color:#1983CB;
background:#D5E3DF;
text-decoration:underline;
}

.section-foot {
margin-top:-6px !important;
}

.container .section-foot {
margin-top:20px !important;
}

#message-center .subsection
{
border-style:none !important;
}


#message-center .subsection .float-holder
{
background:#D5E3DF !important;
border-style:none;

}

#message-center {
background:#EAF2EE !important;
border:none;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
}

#message-center .subsection h3 {
border-bottom-style:none !important;
background:#D5E3DF !important;
font-family:arial !important;
color:#666666 !important;
-moz-border-radius-topright: 1em !important; -moz-border-radius-topleft: 1em !important;
-moz-border-radius-bottomright: 0em !important;
-moz-border-radius-bottomleft: 0em !important;
}

#message-center .subsection h3 a {
color:#1983CB !important;
}

#message-center .subsection h3 img {
display:none !important;
}

#message-center .subsection .float-holder {
-moz-border-radius-bottomright: 1em;
-moz-border-radius-bottomleft: 1em;
}

#subsection-head-m {
background:#D5E3DF !important;
}

#subsection-head-c {
background:#D5E3DF !important;
}

.beacon .first {
