css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("draft.blogger.com") {


.GDAJSF4CF {
    direction: rtl;
    font-family: tahoma;
    font-size: 11px;
    margin: 0 auto;
    min-height: 100px;
    position: relative;
    width: 960px;
}

.GDAJSF4CEB {
    direction: ltr;
    overflow: hidden;
    padding: 20px;
    position: relative;
    z-index: 1;
}
h3 {
    font-size: 13px;
    font-weight: bold;
    margin: 0;
    padding: 0;
    text-align: right;
    text-shadow: 0 1px 1px white;
}
.GDAJSF4CON h4 {
    font-family: tahoma;
    font-size: 13px;
    font-style: italic;
    font-weight: bold;
    margin-bottom: 10px;
}
.GDAJSF4CMJ {
    background: none repeat scroll 0 0 #EBEBEB;
    border-bottom: 1px solid white;
    font-size: 12px;
    left: 0;
    padding: 3px 10px;
    position: absolute;
    top: -1px;
    z-index: 1;
}
.GDAJSF4CBO {
    color: #787878;
    direction: ltr;
    line-height: 24px;
    margin-bottom: 10px;
}


.GDAJSF4CBO {
    color: #787878;
    direction: ltr;
    line-height: 24px;
    margin-bottom: 10px;
}
.GDAJSF4CC .GDAJSF4CAB li {
    direction: ltr;
    line-height: 1.4em;
    margin-bottom: 3px;
}


.GGMMRFPEC .GGMMRFPFB {
    font-family: tahoma;
    font-size: 11px;
}
.editPosts .GGMMRFPME .GGMMRFPBF, .comments .GGMMRFPME .GGMMRFPEB {
    text-align: right;
    word-wrap: break-word;
}
.editPosts .GGMMRFPME {
    border-spacing: 0;
    direction: rtl;
    table-layout: fixed;
    width: 100%;
}
.editPosts .GGMMRFPME tr {
    direction: ltr;
    vertical-align: top;
}
.comments .GGMMRFPME {
    border-spacing: 0;
    direction: rtl;
    table-layout: fixed;
    width: 100%;
}

}
