css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.lordearon.de") {
  .post a[href^="http://wowdata.buffed.de/"] {
    padding-left: 19px !important;
    background: url('http://www.buffed.de/favicon.ico') 1px center no-repeat transparent;
    background-size: 14px;
  }
  .post a[href^="http://www.wowhead.com/"] {
    padding-left: 19px !important;
    background: url('http://www.wowhead.com/favicon.ico') 1px center no-repeat transparent;
    background-size: 16px;
  }
  .post a[href^="http://www.wowwiki.com/"] {
    padding-left: 19px !important;
    background: url(data:image/png;base64,) 1px center no-repeat transparent;
    background-size: 16px;
  }
  .post a[href^="http://www.youtube.com/"] {
    padding-left: 19px !important;
    background: url('http://www.youtube.com/favicon.ico') 1px center no-repeat transparent;
    font-size:small !important;
    background-size: 16px;
  }
  .post a[href^="http://forum.lordearon.de/"] {
    text-decoration: none !important;
  }
  .post a[href^="http://forum.lordearon.de/"]:hover {
    text-decoration: underline !important;
  }
  .post a[href^="http://forum.lordearon.de/"]:after {
    content: " \21D2";
  }
  .post a[href^="http://getmangos.com/"] {
    padding-left: 19px !important;
    background: url('http://getmangos.com/favicon.ico') 1px center no-repeat transparent;
    background-size: 18px;
  }
  .post a[href^="https://github.com/"] {
    padding-left: 19px !important;
    background: url('http://github.com/favicon.ico') 1px center no-repeat transparent;
    background-size: 16px;
  }

  .code {
    font-size: 0.88em !important;
    overflow: auto !important;
    padding: 5px !important;
    width: 98% !important;
    margin-top: 3px !important;
    margin-left: 3px !important;
    border-left-width: 5px !important;
  }

  .quote {
    font-size: 0.85em !important;
  }

  #preview_body {
    height: auto !important;
  }

  .headerbodies {
    background-repeat: no-repeat !important;
  }
}
