/* AGENT_SHEET */
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('clubcooee.com'), domain("80.237.193.10")
{
/*
::=======================================::
@ Club Cooee - FIXWIDTH for myLovelyOne fiy
@ version 1.2, 06.07.2011
@ dwiRianto <dwiriantop@gmail.com>
@ ID dw1Rianto
::=======================================::

CHANGE LOG:

ver 1.2:
  - Multi Language site fix style.
*/

body {color:inherit; }

.block h1 { text-shadow:0 1px 0 #ddd !important; }

#headerLogo { display:block !important; width:100px !important; height:58px !important; position:absolute !important; left:46% !important; top:139px !important; }

#content {
	border:25px solid #96d4ff !important;
	margin-top:85px !important;
	padding-top:20px !important;
	background:-moz-linear-gradient(270deg,rgba(255,255,255,1),rgba(255,255,255,0.96)) !important;
	-moz-border-radius:25px !important;
}

.blockContent  { padding:3px 9px !important; }

#call { background:none !important; }

#seo { display:block !important; width:100% !important; margin:0 auto 20px !important; }

#content.container_12, .container_16 {
	width:86% !important;
	display:block !important;
	margin:0 auto !important;
}

#contentWrap { padding-top:0 !important; }

#headerCountry {
	position:absolute !important;
	right:20px !important;
	z-index:99 !important;
}

#headerLogin { float:right !important; text-align:right !important; }

/* Commnet Input */
#contentWrap select, #contentWrap textarea, #contentWrap input {
	background-image:-moz-linear-gradient(top,rgba(255,255,255,0.96),rgba(246,246,246,1)) !important;
	margin-bottom:10px !important;
}

#contentWrap select, #contentWrap textarea, #contentWrap input,
textarea, input {
	-moz-transition:all 400ms ease-in-out !important;
	-moz-border-radius:5px !important;
}

#contentWrap textarea:hover, #contentWrap input:hover
#contentWrap textarea:focus, #contentWrap input:focus,
#contentWrap textarea:active, #contentWrap input:active {
	border-color:#fd0 !important;
	background-image:-moz-linear-gradient(top,rgba(246,246,246,1),rgba(255,255,255,0.96)) !important;
	-moz-box-shadow:0 0 3px #fd0 !important;
}

.composer {
	background-image:-moz-linear-gradient(top,rgba(239,239,239,0.5),rgba(250,250,250,1)) !important;
}
.composerInputArea {
	margin:0 !important;
}

/* Header */
#headerWrap { background:none !important; }
.grid_14 { width: 80% !important; }

#headerNav { position:relative !important; top:94px !important; }

#headerNav ul li a {
	color:#444 !important;
	margin:0 1px 0 0 !important;
	padding:0 9px !important;
	-moz-border-radius:2px 2px 0 0 !important;
	background-image:-moz-linear-gradient(top,rgba(239,239,239,0.7),rgba(255,255,255,1)) !important;
}

#headerNav ul li a:focus { color:#ff6c00 !important; }
#headerNav ul li a:active { color:#d00 !important; }

/* Left Col */
.container_16 .grid_3 { width:14% !important; }

/* Middle Col */
.container_16 .grid_9 { width:59% !important; padding:0 !important; }
.container_16 .grid_13 { width:80% !important; padding-top:25px !important; }

/* Right Col*/
.container_12 .grid_3, .container_16 .grid_4 { width:20% !important; }

/* Comments*/
.storyComment .header, .noteComment, #subNav li.sep, .message .header {
	background:-moz-linear-gradient(0deg,rgba(255,255,255,0.16),rgba(255,255,255,0.5)) !important;
	border-width:0 30px 1px 0 !important;
	-moz-border-radius:0 5px 5px 0 !important;
	padding-right:3px 5px !important;
}

.message .header .right, .storyComment .header .right { padding-right:9px !important; font-weight:bold !important; }
.note { padding:10xp 20px !important;  margin-bottom:18px !important; }

/* Users */
.heading { border-width:1px !important; border-color:transparent !important; }
#userBar h1 .realName { letter-spacing:0 !important; font-size:11px !important; }
#subNav { position:relative !important; left:-11px !important; }
#subNav li { background:-moz-linear-gradient(0deg,rgba(150,212,255,1),rgba(255,255,255,0.16)) !important; font-size:92% !important; padding:5px 11px !important; margin:1px -15px !important; text-transform:uppercase !important; }
#subNav li:hover {  background:-moz-linear-gradient(0deg,rgba(150,212,255,0.86),rgba(255,255,255,0.16)) !important; }
#subNav li.selected { border-left:12px solid #ff6c00 !important; padding-left:11px !important; background:-moz-linear-gradient(0deg,rgba(255,255,255,0.0),rgba(0,0,0,0.01)) !important; }
#subNav li.sub { padding-left:23px !important; background:none !important; }
#subNav li.sep { margin:3px 0 !important; padding:0 !important; height:1px !important;}

/* Buttons */
a.buttonGfxBig, a.buttonGfxSlick, .headingMore a { background-image:-moz-linear-gradient(270deg,rgba(255,255,255,0.16),rgba(0,0,0,0.5)) !important; line-height:180% !important; }
a.buttonGfxBig { -moz-box-shadow:2px 3px 0px #ddd; }
a.buttonGfxSlick, .headingMore a { -moz-box-shadow:2px 2px 0px #ddd; }
.headingMore a { padding:3px 5px !important; text-decoration:none !important; color:#fff !important; font-weight:bold !important; background-color:#51a8ff !important; }
.headingMore a:hover { background-color:#69aadd !important; }

/* Ease Transitions */
#subNav li, div.pagination a, div.pagination a, a.buttonGfxBig { -moz-transition:all 200ms ease-in !important; }

/* Footer */
#footer { display:block !important; widht:98% !important; font-size:86% !important; }
.container_16 .push_2 { left:0 !important; text-align:left !important; } 
.container_16 .push_1 { float:right !important; left:0 !important; }

}

/* END OF THIS VERSION ... enJOY! ;) */
