@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torn.com") {

  .infoHeader, .meritHeader, #honourBar, .statBox, .statBoxN, span.title, .dragbox h2 { 
    background-color: #999999 !important;
    color: #DDDDDD !important;
  }
  #statusText, .infoBox, #actionBox, .meritBox, #carousel_inner, #facebookLike, #actionWrapper {
    background-color: #BBBBBB !important;
  }
  .title {
    background-position: 0 -240px !important;
    border: 1px solid #999999 !important;
  }

/*
    The following was written by Januska [1413750] to display all medals
*/
  .meritBox,.meritHeader, #carousel_container, #carousel_inner, #carousel_ul {
    width: auto !important; overflow: visible !important; padding-right: 0px !important;}
    #left_scroll, #right_scroll {display: none !important;
  }
}
