/*******************************/

@-moz-document domain("hattrick.org") {


#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
        /*background: url(page/top.png) bottom left no-repeat !important;*/
}

#footer a
{
        color: white !important; 
}

#hattrick,#hattrickNoSupporter 
{
        margin:auto!important; 
        position:relative;
        left:-5px;width:993px!important;
}

#ticker
{
	position: absolute !important;
	top: 45px !important;
	left: 365px !important;
}


#ticker div a
{
	padding-bottom: 2px !important;
	color: #000000 !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}

#shortcuts
{
	margin-top: -8px !important;
        margin-left: 10px !important;
	margin-right:380px !important;
        float: right !important;
        width: 266px !important;
}

#teamLinks
{
        color: #8e0000 !important;
        margin-top: 36px !important;
	margin-left: 0px !important;
}

div#teamLinks a
{
        color: #ffffff !important;
}

/*Users online - week - date - time*/

div[id="online"] 
{
        color: #ffffff !important; 
        float:right!important;
        margin-right:0pt!important;
        margin-top:27pt!important;
}
div[id="time"]
{
        color: #ffffff !important;
        float:right!important;
        margin-right:10pt!important;
        margin-top:27pt!important;
}

/*Page background*/
body
{  
  background: #4a4a4a !important;
}

/*centre title*/
h1 
{
        color: #4d0149 !important;
        border-bottom: solid thin darkgrey !important;
        background: #eeeeee !important;
}

/*all boxHead*/
h2 
{
        /*color: white !important;*/
        /*border-bottom: solid thin #eeeeee !important;*/
        /*background: #c70000 !important;*/
}

/*centre news*/
.boxBody h2 
{ 
        color: #4d0149 !important;
        border-bottom: solid 1px black !important;
}


/*-----------boxHeads-----------*/

/*Left menu*/
.subMenuBox .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png)!important;   
}

.subMenuBox .boxHead .boxLeft
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png) !important;      
}

/*Centre box*/
.main .boxHead h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png)!important;
}

/*Right menu*/
.sidebarBox h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png)!important;
}

/* Press, GB, Team events */
.main .mainBox h2 
{
	border-bottom: solid 1px #000000 !important;
	background-color: #fff !important;
	color: #4d0149 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}


/*Central header paths*/

h2 a 
{
        color: #ffffff !important;
}

h2 a:hover 
{
        color:#f6d206 !important;
}


/*News and team events date*/

.date
{
	color: #c2a8a8 !important;
	padding-right: 8px;
	font-weight: 400;
}

/*Left Menu titles*/
h3 
{
        color: #4d0149 !important;
        border-bottom: solid thin #808080 !important;
        background: #eeeeee !important;
}

/*General Links*/
a
{
        color: #82017b !important;
	text-decoration: none !important;
}

/*----subMenu and sidebar links----*/
.subMenuBox a, .subMenuBox a, .sidebarBox a 
{
	text-decoration: none !important;
	color: black !important;
}

/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}


/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}

#stageLogo {
        background: none !important;
}

/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}


/* central page background */
#content
{
        background-image: none !important;
        background-color: #c4c4c4 !important;

}

/*Image header (banner)*/
#header 
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/RM_banner2.jpg) !important;
	height: 125px !important;
	padding-top: 50px !important;
}

/*--------Top menu settings--------*/
#menu
{
	height: 30px !important;
        width: 975px !important;	
        margin-top: 3px !important;
        margin-left: -14px !important;
        bottom: 0px !important;
        background: none !important;
}

#menu a
{
	line-height: 30px !important;  
        color: #ffffff !important;
        /*-moz-border-radius: 2% !important;*/        
        /*background: #808080 !important;*/
}

div#menu a:hover 
{
        color:#FFFFFF !important; background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/over_menu-1.jpg) !important;
        line-height: 30px !important;
}

a:hover
{
	color: #000000 !important;
}

/*bottom white line*/
div[id="bottom"]
{
        background: #c4c4c4 !important;
}


/*---------Forum---------*/
.cfHeader
{
        background:  url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png) !important;
}

#cfPollHeader
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png) !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}

.cfWrapper
{
	border: solid 1px #000000 !important;
	margin: 0 0 12px 0;
}

.cfWrapper div.signature a, div.signature-trunc a, div.signature-expand a {
    color: #999999 !important;
    text-decoration: underline !important;
}

.cfHeader a
{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
}

.cfWrapper a
{
	text-decoration: none;
        /*color: white !important;*/
}

.cfUserInfo a
{
        /*color: #de4747 !important;*/
}

.cfMessage a
{
	/*color: #de4747 !important;*/
}

.folderHeader a:link 
{
	color: #4d0149 !important;
	font-weight: bold !important;
}

.cfFooter a
{ 
        color: black !important;
}


.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;
}

.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;
}

.folderItem div.unignore:hover
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;
}

/*Ads section*/

div[class="insertHeader"]
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/rm_boxhead2.png) !important;
}

div[class="insertFooter"]
{
        background: #ffffff !important;
}


/*shortcuts*/

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}


/* MatchLineup */
.name a
{
	color: #277f31 !important;
}

/* Tabs Match orders*/
.ui-tabs .ui-tabs-nav li a { float: left; text-decoration: none; padding: 4px 10px 3px 10px; color:#eee; text-decoration:none;  color: white !important;}

/* Tabs Match line up */
.LinkTabActive, .LinkTabNotActive {
    background-color: #2E741B;
    color: #FFFFFF !important;
    float: left;
    font-size: 1em;
    font-weight: 700;
    padding: 0 4px;
    text-decoration: none;
}


/*Foxtrick View by ID*/


#ft_rapidid, #ft_rapidid a
{
    margin-top: 36px !important;
    margin-right: 18px !important;
    color: #ffffff !important;
}

/*Foxtrick TeamPopupLinks*/

ul.ft-popup-list 
{
    color: #000000 !important;
    background-color: #fff !important;
    border: solid 1px #000000 !important;
}

ul.ft-popup-list a
{
    /*color: #000000 !important;*/
}


ul.ft-popup-list > li > a, ul.ft-popup-list > li > span {
    color: #000000 !important;
    cursor: pointer;
    display: block !important;
    font-weight: normal !important;
    padding: 0 !important;
    text-align: left;
    text-decoration: underline !important;
}
ul.ft-popup-list > li:hover {
    background-color: #C3E7C7 !important;
}


div[class*="ad_"]
{
        display:none !important;
}

}
