css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tumblr.com") {



#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,

#dashboard_post_queue, #dashboard_followers, #dashboard_members{



background-image:url('http://i54.tinypic.com/iydbo6.jpg') !important;




background-repeat:repeat;



background-attachment:fixed;


color:#000000!important;



font-size:12px !important;








}



h1,h2,h3,h4,h5,h6{color:#000000 !important}




a, a:visited{color:#000000 !important}





a:hover{color:#959494 !important}



#next_page_link, #previous_page_link,

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    border:0px!important;

    background: rgbargba(245,237,214,0.8) !important;

}



.post_question, 

#left_column #posts .post .permalink {

    background: rgba(245,237,214,0.8) !important;

}



.post_question_nipple, .arrow{

    background:transparent!important;}



#search_controls{

border-bottom-color: transparent !important;

    border-bottom:none!important;

    border-top:none!important;

    border:none!important;

    background: rgba(245,237,214,0.8) !important;

}


#help_docs:hover, 

#all_of_tumblr:hover,

#everyone_i_follow:hover,

#my_posts:hover{

    background: rgba(245,237,214,0.8) !important;

border-bottom:0px #014075 solid !important;

border-top:0px #014075 solid !important;

}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background: rgba(245,237,214,0.8) !important;

background-image:none !important;

border:0px !important

}


#nav .nav_item .nav_menu:hover{color:#959494 !important;

    background: rgba(245,237,214,0.8) !important;

}





#nav .nav_item .nav_menu a {

color:#000000 !important;

border-top-color:0px #000 !important;

}




#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;

  }







#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,

#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {


background: rgba(245,237,214,0.8) !important;




}







#dashboard_edit_post{



background-color: #ffffff !important



}







#right_column .dashboard_nav_item {



border-bottom:0px solid #ffffff !important;



background-image:none  !important



}







#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fdba51 !important;



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#fdba51 !important ;



                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }



#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#ffffff !important;






                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }







.dashboard_switch_blog_menu_header{



background-color:#ffffff !important;



color:#fdba51 !important;



}



.dashboard_switch_blog_menu_item{



background-color:#ffffff !important;



color:#fdba51 !important;



border:0px !important



}



.dashboard_switch_blog_menu_footer{



background-color:#ffffff !important;



color:#fdba51 !important;



}



.dashboard_switch_blog_menu_wrapper{



background-color:#ffffff !important;



}







#new_post_notice_container{



color:#ffffff !important



}







#new_post_notice_container a{



color:#fdba51 !important



}







#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{



background-image:none !important



}























ol#posts li.post.new_post {



background: #ffffff url('http://assets.tumblr.com/images/new_post_icons_no_labels.png') center center no-repeat;



}







img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{





    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;

}





.new_post_label{



font-size:14px !important;



padding-top:7px !important;



font-family:georgia !important;



font-style: !important;



text-transform: lowercase !important;



color:#000000 !important;



letter-spacing:1px !important



}







#left_column #posts .notification {



                        background-color:#ffffff !important;



                        color:#fdba51 !important;



                        border-bottom-color: #ffffff !important



                    }







#left_column #posts .notification .block {



                            color:#fdba51 !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #ffffff !important;



                    }



#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#ffffff !important;



                    }



#left_column #posts .notification a {



                        color:#ffffff !important;



                        border-bottom:0px !important;



                    }



#left_column #posts .notification blockquote {



                        border-left-color:#ffffff !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#ffffff !important;




                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }



.notification_type_icon{display:none !important}

.dashboard_switch_blog_menu_header{

background: rgba(245,237,214,0.8) !important;

color:#000000 !important;

border-bottom:0px solid #eee !important;

}



.dashboard_switch_blog_menu_item{

background: rgba(245,237,214,0.8) !important;

color:#000000 !important;

border-bottom:0px solid #eee !important;

}


.dashboard_switch_blog_menu_footer{

background: rgba(245,237,214,0.8) !important;

color:#000000 !important;

border-bottom:0px solid #eee !important;

}

.dashboard_switch_blog_menu_wrapper{

background: rgba(245,237,214,0.8) !important;

border-bottom:0px solid #eee !important;

}





#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{background-image:none !important}



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

   background: rgba(245,237,214,0.8) !important;

  }



*{text-shadow:none !important; color:#000000 !important;}


#pagination a {

background: rgba(245,237,214,0.8) !important;

border-bottom: 0px solid #eee;

}
}
