css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
 Morpfh 4 you
 v.0.2-fxn
*/
@-moz-document 
url-prefix(http://www.tldp.org/LDP/),
url-prefix(http://tldp.org/LDP/),
url-prefix(http://tldp.org/HOWTO/),
url-prefix(http://www.tldp.org/LDP/)
{
body, html {
    width : 375pt !important;
    margin: 7.5pt auto !important;
    }
/* H e a d i n g s */
h1,
h2,
h3,
h4 {
    font-family : 'Georgia', 'Serif' !important;
    border-style: solid !important;
    }
h1 {
    color       : #df9c1b !important;
    border-width: 0 0 7.5pt 0 !important;
    border-color: #df9c1b !important;
    }
h2 {
    color       : #df851b!important;
    border-width: 0 0 3.75pt 0!important;
    border-color: #df851b!important;
    }
h3 {
    color       : #df601b !important;
    border-width: 0 0 1.5pt 0 !important;
    border-color: #df601b !important;
    }
h4 {
    color       : #df4e1b!important;
    border-width: 0!important;
    }
/* T a b l e  h e a d i n g s */
th {
    color: #351d4f !important;
    }
/* A n c h o r s */
a:link, 
a:visited, 
a:active {
    text-decoration: none !important;
    }
a:hover {
    text-decoration: underline !important;
    }
/* E x p r e s s e d */
b {
    color: #df3b1b !important;
    }
em {
    color: #351d4f !important;
    }
/* T e r m i n a l */
pre, 
tt {
    font-family:'Courier New', 
            'DejaVu Sans Mono', 
            'Nimbus Mono L', 
            'Lucida Console',
            'Monospace'
            !important;
    }
pre {
    background-color: #1c1c1c !important;
    color           : #d0d0d0 !important;
    font-size       : 70% !important;
    display         : inline-block;
    padding-right   : 1em !important;
    }
tt {
    font-size: 100% !important;
    }
p tt {
    font-size: 88% !important;
    }
table tt {
    font-size  : 100% !important;
    }
blockquote code pre {
    font-size  : 88% !important;
    padding-top: 10pt !important;
    }
/*
blockquote code pre:first-line {
    color: #3366ff;
    }
*/
/* P a r a g r a p h s - and o t h e r */
p {
    font-family: 'Arial', 'Sans-serif' !important;
    font-size  : 90% !important;
    }
h1 + p:first-letter,
h2 + p:first-letter,
h3 + p:first-letter,
h4 + p:first-letter,
blockquote + p:first-letter {
    color      : #3c3c3c !important;
    font-size  : 160% !important;
    font-style : normal !important;
    font-weight: 500 !important; 
    font-family: 'Georgia', 'Serif' !important;
    float      : left !important;
    }
/* T O C's */
dl dd {
    font-family: 'Arial', 'Sans-serif' !important;
    white-space: pre !important;
    }
dl.LOT dt {
    font-family: 'Arial', 'Sans-serif' !important;
    }

dl.LOT dt b {
    font-family: Serif !important;
    }
/* F i x e s */
pre.screen font[color*="#000000"] {
    color: #afafaf !important;
    }
div.figure b {
    font-family: 'Georgia', 'Serif' !important;
    }
table[bgcolor*="#e0e0e0"] {
    background-color: #1c1c1c !important;
    border          : 2.75pt ridge #afafaf !important;
    }
/* F i x d'  p o s  N a v i g a t i o n */
div.NAVFOOTER {
    opacity           : 0.87;
    -moz-border-radius: 7pt 7pt 0 0;
    -moz-box-shadow   : 5pt 0 9pt #555555;
    background-color  : #f0f0f0;
    position          : fixed;
    bottom            : 0;
    width             : auto;
    margin            : 0 10pt !important;
    padding           : 0 2em 0 2em !important;
    /*left: 0;*/
    }
div.NAVFOOTER table {
    font-size: 60% !important;
    }
div.NAVFOOTER a {
    color: #000033 !important;
    }
div.NAVFOOTER td {
    color: #003300 !important;
    white-space: pre;
    }
div.NAVFOOTER a:after {
    content: " [" attr(accesskey) "]";
    }
div.NAVFOOTER hr {
    display: none;
    }
/* Make room for fixed navigation at end of page */
body:after {
    content   : "0_o";
    display   : block !important;
    clear     : both !important;
    margin    : 5em 0 !important;
    text-align: center !important;
    }
} /* EOF moz-document */
