css:
@-moz-document url-prefix("https://nyaa.si/"), url-prefix("https://sukebei.nyaa.si/")
{

body
{
	background-image: url(https://i.imgur.com/DKje6Rb.png) !important;
	font-family: verdana !important;
}

.navbar
{
	background-image: none !important;
	background-color: rgba(0, 0, 0, 0.5) !important;
	position: relative !important;
	border: none !important;
}

div.content
{
	background-color: rgba(255, 255, 255, 0.95) !important;
	position: relative !important;
	top: -45px !important;
	width: 100% !important;
	border: none !important;
}

#chatbox
{
	position: relative !important;
	top: -50px !important;
	width: 100% !important;
	border: none !important;
}

.trusted.tlistrow
{
	background-color: rgba(50, 75, 100, 0.5) !important;
}

.remake.tlistrow
{
	background-color: rgba(100, 50, 50, 0.5) !important;
}

.aplus.tlistrow
{
	background-color: rgba(50, 100, 50, 0.5) !important;
}

a
{
	text-decoration: none !important;
	color: rgb(0, 0, 0) !important;
}

#tabnav > li > a
{
	color: #ffffff !important;
}


#tabnav > li > a:hover
{
	color: rgba(255,255,255,0.8) !important;
}

a:hover
{
	color: rgba(0,0,0,0.5) !important;
}

.page:hover
{
	background-color: rgba(50, 100, 150, 0.5) !important;
}

div[style="float:left;width:100%;"], div[style="float:right;width:11.9%;"], .main > div[style="float:left;"], a[target="_blank"]
{
	display: none !important;
}

}
