css:
@namespace url(http://www.w3.org/1999/xhtml);

/* BTW - Visited links are deleted when you clear your history */

@-moz-document 
domain(wikipedia.org)
{
[class*="content"] a:visited {color:#a5a !important } 
}



@-moz-document 
domain(zap.co.il)
{
div.ProdInfo a:visited {color: purple !important } 
}

@-moz-document 
domain(mixcloud.com)
{
a.mx-cc-item-name:visited {color: purple !important } 
a.mx-cc-item-creator:visited {color: purple !important } 
a.mx-link:visited {color: purple !important } 
}


@-moz-document 
url-prefix(http://www.youtube.com/subscription_manager)
,url-prefix(https://www.youtube.com/subscription_manager)
{
a.subscription-title:visited {color: purple !important } 
}
