css:
@-moz-document url-prefix("http://my.deviantart.com/profile/") {

body {
background:#6A7872 !important;
}

#top h1, .ttr  {
background-color:#6A7872 !important;
}
.tabbar {
background:#D5E3DF;
}

.tabbar strong, .tabbar a {
border:none !important;
background:#D5E3DF !important;
color:#1983CB !important;
font-size:16px !important;
font-weight: bold !important;
}

.tabbar a:hover {
text-decoration:underline !important;
}

.tabbar strong {
color:#000000 !important;
}
textarea, select {
background:#EAF2EE !important;
}

.text {
background-color:#EAF2EE !important;
}

.tabbar a:hover, .tabbar a:active {
color:#1983CB;
background:#D5E3DF;
text-decoration:underline;
}

.section-foot .aside-right, .section-foot .aside-left {
background:#EAF2EE !important;
}

.section-foot .aside-right:hover, .section-foot .aside-left:hover {
background:#FFFFFF !important;
}

.ie-floatfix {
background:#D5E3DF !important;
border:none !important;
-moz-border-radius-topright: 1em;
-moz-border-radius-topleft: 1em;
padding:1em;
}

#window .trailing,  .alt1 {
background:#EAF2EE !important;
border:none !important;
}

.alt1 {
background:#D5E3DF !important;
}

.section-foot {
background:#D5E3DF !important;
border:none !important;
-moz-border-radius-topright: 0em !important;
-moz-border-radius-topleft: 0em !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
padding:1em;
}

.alt2, .notice img {
background-color:#EAF2EE !important;
}

.dev-thumbnails, .dev-thumbnails .dev-showtext p.dev .option-selected {
background:#D5E3DF !important;
}

.shadow, .dev-text {
background:#EAF2EE !important;
}

.dev-title {
color:#000000 !important;
}

fieldset {
font-size: 12px !important;
border:none !important;
background:#D5E3DF !important;
-moz-border-radius-topright: 1em !important;
-moz-border-radius-topleft: 1em !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
}

#nav-east {
display:none !important;
}

.output-primary {
width:100% !important;
}

.button {
background:#B9D0CA !important;
border:none !important;
}

}
