    border-bottom: 1px solid #EBDAB1;
}

ol#posts li.post .permalink {
    background: rgba(14,67,11,0);
}

ol#posts li.post.new_post {
    background: #ffffff url('http://i56.tinypic.com/2moxuna.png')
    center center no-repeat;
}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}

