  text-decoration: none !important;
  }

div#charts div.chartbar span {
  font-size: 100% !important;
  font-weight: 500 !important;

  }

div.chartbar span {
  background: #484C3A!important;
color: #fff!important;

  }

div#charts #content,
div.fixed #content {
  margin: 0 0 0 0 !important;
padding: 10px 15px 15px 15px !important;
  border: none !important;
-moz-border-radius-topright: 25px !important;
-moz-border-radius-topleft: 25px !important;

}


div.sic #content, 
div.fiflufi #content {
  margin: 0 0 0 0 !important;
  border: none !important;

  }

.horizontalOptions ul li a {
color: #aaa!important;
}

.horizontalOptions ul li a:hover {
color: #fff!important;
background-color: #484C3A !important;
text-decoration: none !important;
