}


@-moz-document domain(last.fm/user/*/library), domain(last.fm), domain(lastfm.com.br), domain(lastfm.de), domain(lastfm.es), domain(lastfm.fr), domain(lastfm.it), domain(lastfm.pl), domain(lastfm.se), domain(lastfm.com.tr), domain(lastfm.ru), domain(lastfm.jp), domain(cn.last.fm) {



#libraryTop {
background-color: #fff!important;

}
 
ul.artistsLarge li {
margin: 5px 0px -15px 15px !important;

}

#libraryNavigation {
border-bottom: solid 1px #ddd!important;

}

table.tracklist th, table.tracklist td {
border-bottom: solid 1px #ddd !important;
}

table.tracklist tr.first td,
.playlists td {
border-top: solid 1px #ddd!important;
}

table.tracklist td.date {
border-right: solid 1px #ddd!important;
}

div.sic #content {
background: #fff!important;
    -moz-border-radius-topleft: 25px !important;
    -moz-border-radius-topright: 25px !important;
    -moz-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 13px !important;
}
    ul#langSelector{
        background: #ffffff !important;
        border: 1px solid #777 !important;
        -moz-border-radius-topright: 2px !important;
        -moz-border-radius-topleft: 2px !important;
        -moz-border-radius-bottomright: 2px !important;
        -moz-border-radius-bottomleft: 2px !important;
    }
    #langSelector a {color: #777 !important; font-weight: 600 !important; font-size: 10px !important;}
    ul#langSelector a:hover{color: #ffffff !important; background: #3E4430!important;}
    #langSelector li a:hover {color: #fff !important;}

#idBadger {
    margin-left: 4px !important;
    position: relative !important;
}

#idBadgerUser {
    position: relative !important;
    display: -moz-inline-box !important;
    display: inline-block !important;
    background-position: 10px 160px !important;
    background-repeat: no-repeat !important;
    font-size: 13px !important;
    height: 13px !important;
    line-height: 13px !important;

    color: #484C3A!important;

    text-decoration: none !important;
    text-shadow: none !important;
}

#idBadgerUser:hover {
    color: #70603F!important;
}

#idBadgerUser img {
    display: block !important;
    position: absolute !important;
    top: 3px !important;
    left: 3px !important;
    height: 16px !important;
    width: 16px !important;
    -webkit-border-radius: 2px !important;
    -moz-border-radius: 2px !important;
}


#idBadgerDropper {
    display: -moz-inline-box !important;
    display: inline-block !important;
    width: 21px !important;
    background-position: -2px -420px !important;
    background-repeat: no-repeat !important;
    font-size: 11px !important;
    height: 11px !important;
    line-height: 11px !important;
    padding: 5px 0 7px 0 !important;
    text-decoration: none !important;
    text-indent: -900px !important;
}
#idBadgerDropper span {
    visibility: hidden !important;
}




#idBadgerDropDown {
    background: #FFFfff !important;

    border: 1px solid #ccc !important;
    -webkit-border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px !important;
    -moz-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px !important;
}

#idBadgerDropDown a {
    display: block !important;
    font-size: 11px !important;
    line-height: 22px !important;
    padding: 0 7px !important;
    color: #999 !important;
    text-decoration: none !important;
    cursor: pointer !important;
}

#idBadgerDropDown a:hover {
    background: #484C3A!important;
    color: #fff !important;
    text-decoration: none !important;
    cursor: pointer !important;
}


span.calSheet span.month {
    background: #484C3A!important;
    border: 0px solid #586232!important;
    color: #fff !important;
    text-transform: uppercase !important;
    line-height: 1.1em !important;
    font-weight: bold !important;
    text-align: center !important;


}
#header a, #headLinks a, #headLinks, .lfmBlack #headLinks, .lmBlack #headLinks a {
    color: #969696!important;

        text-shadow: none !important;
        text-decoration: none !important;
    }
    #header li a:hover {
    color: #3E4430!important;
background: transparent!important;
height: 15px !important;
-moz-border-radius: 5px !important;


    }



}
