{
/*Theme par S9Clay*/
body {
background : url("http://www.profilebrand.com/imgs/layouts/8sweet-treats/2281/2281_bg.png") bottom left fixed !important ;
}

.brand-mamabar {
background : #F8C7D7 !important ;
color : #E6428A !important ;
}

.brand-mamabar a, #header a{
color : #E6428A !important ;
}

a.brand-mamabar-active {
color : #F5A0BC !important ;
}

brand-mamabar-more-a {
background : transparent !important ;
}


/*Bannière*/

#header {
background : #C1C1C1 none !important ;
border-radius : 25px 25px 0 0 ;
border : 2px dashed grey !important ;
}

#main, #ban {
background : transparent none !important ;
border : none !important ;
}

#content {
background : #dad9d9 !important ;
border-left : 2px dashed grey !important ;
border-right : 2px dashed grey !important ;
color : black !important ;
}

.content {
color : white !important ;
}
.content a {
color : #E6428A !important ;
}



#footer {
background : #C1C1C1 none !important ;
border-radius : 0 0 25px 25px ;
border : 2px dashed grey !important ;
}

.sidebar {
background :#F5A0BC !important ;
}

.sidebarHeader, .rcModuleHeader {
background : #E64289 !important ;
border-radius : 15px 15px 0 0 ;
}

.rcModuleTopLeft, .rcModuleTopRight {
background-color : #E64289 !important ;
}

.gamesRoomLiteModule .rcModuleContent, .rcModuleContent{
border-left : 2px solid #F4ACCB !important ;
border-right : 2px solid #BD4579 !important ;
border-bottom : 2px solid #BD4579 !important ;
border-radius : 0 0 20px 20px ;
}

.rcModuleContentOuter {
border-left : 2px solid #ED74A8 !important ;
border-right : 2px solid #6B2E48 !important ;
border-bottom : 2px solid #ED74A8 !important ;
border-radius : 0 0 22px 22px ;
}

.rcModuleBottomLeft, .rcModuleBottomRight {
background : none !important ;
}

.sidebarTable {
border : 1px grey dotted !important ;
border-radius : 15px 15px 0 0 ;
box-shadow : 4px 4px 5px grey ;
}

ul.dropdown {
background :#F8C7D7 !important ;
border : 1px dashed grey !important ;
}

.contentModuleHeaderAlt {
background : #F5A0BC !important ;
}

.contentModuleHeader {
background : #E64289 !important ;
}

*[bgcolor="#ffffcc"], td[bgcolor="#ddddaa"]  {
background : #E64289 !important ;
border-bottom : 1px dotted grey !important ;
}

*[bgcolor="#ffffee"] {
background : #F5A0BC !important ;
}

*[bgcolor="e3e7e7"] {
background : #F8C7D7 !important ;
}

.contentModuleContent td[style="background-color:white"] {
background : #F8C7D7 !important ;
}

.contentModuleContent td[style="background-color:#efefef"] {
color : black !important ;
}

.contentModuleContent, .blistSmall, .stats_title, .rcModuleContentInner, td[height="300"], td[style="border: 2px solid #686868;"]  {
color : grey !important ;
}

#user_diff {
color : #F5A0BC !important ;
}

#ctp_image_pad {
background : #F5A0BC none !important ;
border : 1px grey dotted !important ;
border-radius : 15px ;
box-shadow : 4px 4px 5px grey ;
}

#ctp_image_pad:hover {
background : #F8C7D7 none !important ;
}

.btnLeft, .btnRight {
background : transparent none !important ;
width : 0 !important ;
}

.btnCenter {
background : #F8C7D7 none !important ;
border : 1px grey ridge !important ;
border-radius : 10px !important ;
padding-left : 10px !important ;
padding-right : 10px !important ;
}

.btnCenter:hover {
background : #E64289 none !important ;
color : white !important ;
}

.marqueeSlotOn, .marqueeSlotOn A:link, .marqueeSlotOn A:hover, .marqueeSlotOn A:visited {
background : #F8C7D7 !important ;
color : black !important ;
}

.marqueeSlot.sf, .marqueeSlot.sf  A:link{
background :#F5A0BC !important ;
color : white !important ;
}

.moreLinkAlt, .moreLink {
border : 1px dotted white !important ;
}
a.moreLink{
color : white !important ;
}

.sidebarModule {
border : none !important ;
}

form[action="process_ul_submission.phtml"] {
color : black !important ;
}

font[color="red"] {
background-color : black !important ;
}

td[bgcolor="#F6F6F6"] {
color : darkgrey !important ;
}

tr[bgcolor="#ffffcc"] a{
color : white !important ;
}


table [bgcolor="#aaccaa"]{
color : #E64189 !important ;
}
}
