css:
@-moz-document url-prefix('http://twitter.com') {
.component h2 span.user-stat-link {
margin-left: 5px;
font-size: 11px;
font-weight: bold;
background: #E7E9ED;
border: 1px solid #D3D7DE;
padding: 1px 4px;
border-radius: 5px;
box-shadow: 0 1px 0 white;
}

.component h2 {
font-size: 11px;
font-weight: bold;
line-height: 2em;
}

div.latest-tweet .tweet-image, div.latest-tweet .tweet-image img {
width: 32px;
height: 32px;
border-radius: 3px;
position: relative;
}

div.latest-tweet .tweet-image a:after, .user-thumb-list-member a::after, .tweet-image:after, .user-profile-link.user-thumb:after {
position: absolute;
top: 0;
bottom: 0;
left: 0;
right: 0;
content: '';
display: block;
border-radius: 3px;
box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.55),inset 0 -1px 1px rgba(0, 0, 0, 0.3),0 1px 0 #fff;
}

.dashboard .tweet {
border-bottom: 1px solid #C2C8D1;
padding-bottom: 10px;
}

li.user-thumb-list-member {
position: relative;
line-height: 0;
}

.tweet-image {position: relative;}
.tweet-image img, li.user-thumb-list-member img {border-radius: 3px;}
div.latest-tweet .tweet-image a:after, .stream-tweet .tweet-image {border-radius:5px;}

#session img {
border-radius: 3px;
border: 1px solid rgba(0, 0, 0, 0.3);
box-shadow: 0 1px 0 rgba(255, 255, 255, 0.075);
}

div.latest-favorite .tweet-user-name i, div.recently-listed-in i {display:none}

.user-static-list-item .user-thumb img, .user-small-list-item .user-thumb img {
border-radius: 3px;
}

hr {
border-top: 1px solid #C2C8D1;
}

.component div.definition+hr, .component div.definition {display:none}

.dashboard ul.trends li:nth-child(5+1) {
float:right
}


}
