css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://79.143.250.1/cgi-bin/wiki.pl") {

div.old { background-color:#FFFFAF }
div.new { background-color:#CFFFCF}
img.logo {
    background-color:#fff;
}
li img, img.smiley, .noborder img {
    background:#fff;
    color:#000;
}
span.author {
    color: #501;
}
body {
    color:#000 !important;
    /* background:#EAF3FF !important; */
    background:#FFEAAE !important;
}
a {
    color:#473C8B !important;
}
p a, li a {
    font-weight:normal !important;
    text-decoration: underline !important;
}
a:visited {
    color:#473C8B !important;
}

.rc .author {
    color: #666;
}
.rc .type {
    background:#fff; /* covers language if no summary */
}
span.gotobar a, div.sidebar a {
    text-decoration: none !important;
    font-weight: bold !important;
}
div.header h1 a:hover, h1 a:hover,
h2 a:hover, h3 a:hover, h4 a:hover,
a:hover, span.caption a.image:hover {
    background:#473C8B !important;
    color:#FFF !important;
    text-decoration: none !important;
}
span.gotobar a:hover, div.sidebar a:hover {
    border-left: 5px solid #FFA500 !important;
}
a.image:hover {
    background:inherit;
}
a.image:hover img {
    background:#ccc;
}

p a.definition {
    color:#666;
    border-bottom: 2px solid #000000;
}
p a.definition:hover {
    background:#000000;
    color:#FFFFFF;
}

h1, h2, h3, h4,
h1 a, h2 a, h3 a, h4 a,
h1 a:visited, h2 a:visited, h3 a:visited, h4 a:visited {
    color:#473C8B !important;
}
h1, h2 { border-bottom: 2px solid #FFA500 !important; }
h3 { border-bottom: 1px dashed #FFA500 !important; }
div.header h1 {
    border-bottom: 5px solid #FFA500 !important;
}
hr {
    color:#BBB;
    background-color:#000;
}
pre {
    border: #473C8B 2px solid !important;
    background-color: #F8FEFF !important;
    color: black;
}
body.magenta a { color:#639; }
div.cal pre {
    background-color:#ffe;
}
div.cal a.local {
    background-color:#fee;
}
div.cal a.today { background-color:#fdd; }
div.cal a:hover {
    color:#fff;
    background-color:#000;
}

}
