css:
@-moz-document domain("gaiaonline.com") {

/* Customize the welcome message. */
.hud-item.avatarName span[style*="bold"]:before {
  content: "Logged in as "; }

#gaia_menu_bar,
.mega-menu .main_panel,
#gsearchContainer .yui-ac-content,
#notifyBubbleContainer {
  background: #f5f5f5 !important; }

.hud-account,
.panel-open .megamenu-section-trigger,
.megamenu-section-trigger:hover,
.megamenu-section-trigger:hover,
#gsearch_ac_menu.ta_ic.ta_submit_menu,
#gsearchContainer .yui-ac-bd h3,
#gsearch_submit_menu,
#notifyBubbleContainer strong {
  background: #444444 !important; }

.main_panel .panel-links > li > a:hover,
.main_panel .gaia_menuitem_zomg:hover,
.main_panel .gaia_menuitem_monstergalaxy:hover,
.main_panel .gaia_menuitem_fishing:hover,
.complex-sublist li > a:hover,
#gsearchContainer .yui-ac-bd .name:hover,
#gsearchContainer .yui-ac-bd .yui-ac-highlight,
#notifyBubbleContainer .notify_item_link:hover {
  background: #eaeaea !important; }

#gsearch_submit_menu .yuimenuitem-selected:hover a {
  background: #555555 !important; }

.hud-item.avatarName span[style*="bold"],
.hud-account .hud-item-list a,
#gold_amt:after,
#cash_amt:after,
#achievements-points:after,
#nav > li:hover > a,
#gsearch_ac .ta_submit,
#gsearchContainer .yui-ac-bd h3,
#gsearch_submit_menu a,
#notifyBubbleContainer strong:after {
  color: #f5f5f5 !important; }

#nav > li > a,
.main_panel .panel-links a,
#gsearchContainer .yui-ac-bd span.name,
#notifyBubbleContainer .notify_item_link {
  color: #444444 !important; }

.hud-account .hud-item-list a:hover,
#gold_amt:hover,
#cash_amt:hover,
#achievements-points:hover,
#gold_amt:hover:after,
#cash_amt:hover:after,
#achievements-points:hover:after,
#gsearchContainer .yui-ac-bd .name .match {
  color: #40b3df !important; }

#gsearch_ac .ta_submit {
  border-color: #f5f5f5 !important; }

.main_panel,
#gsearch_ac_input,
.inactive #gsearch_ac_input,
#notifyBubbleContainer {
  border-color: #444444 !important; }

.main_panel .panel-links > li > a:hover,
.main_panel .gaia_menuitem_zomg:hover,
.main_panel .gaia_menuitem_monstergalaxy:hover,
.main_panel .gaia_menuitem_fishing:hover,
.complex-sublist li > a:hover,
#gsearchContainer .yui-ac-bd .name:hover,
#notifyBubbleContainer .notify_item_link:hover {
  border-color: #40b3df !important; }

.hud-account .hud-item-list .pipe,
.end-cap,
.hud-stats .hud-spacer,
.hud-stats #cta-refill,
.hud-stats #cta-get_gaia_cash,
#achievements-unlocked-list,
.goldMessage,
.fbconnect,
.megamenu-divider,
#nav > li > a > .lt_selector,
#nav > li > a > .rt_selector,
#nav > li > .home-icon,
.panel-title,
.panel-link-descrip,
.complex-sublist span,
.complex-sublist p,
.panel-img,
.panel-more,
.mega-menu .panel_bottom,
#zomg_img,
#zomg_links #monstergalaxy_img,
#community_menu .panel_community_popforums:nth-of-type(2),
.megamenu-menu-color,
.ta_ic.search_ic,
.ta_ic.search_ic_end,
#gsearch_ac .yui-ac-bd .ta_ic,
#gsearch_ac .yui-ac-bd .ta_friend,
#notifyBubbleContainer h3,
#notifyBubbleContainer .endMessageContent {
  display: none !important; }

#gaia_header .hud-item-list,
.megamenu-no-panel,
.megamenu-standard-menu,
.main_panel_container,
.panel-left,
.panel-right,
#community_links,
#community_links li,
#flashspace_worlds_links,
#flashspace_worlds_links li,
#zomg_links,
#gsearch_ac_submit,
#gsearch_ac_menu,
#gsearchContainer .yui-ac-content,
#gsearchContainer .yui-ac-bd ul {
  margin: 0 !important; }

.hud-stats .padded-icon,
.main_panel,
.main_panel a,
#gsearchContainer .yui-ac-bd li,
#notifyBubbleContainer strong,
#notifyBubbleContainer .messageContent {
  padding: 0 !important; }

.megamenu-section-trigger,
#gsearch_ac_input,
#notifyBubbleContainer .notify_item_link {
  margin: 0 !important;
  padding: 0 !important; }

#gaia_header .hud-item-list,
#gsearch_submit_menu {
  border: none !important; }

.yui-ac-ft {
  height: 0 !important; }

.hud-stats,
.hud-stats .hud-item a,
.megamenu-no-panel,
.megamenu-standard-menu,
.panel-left,
.panel-right,
#gsearchContainer .yui-ac-content .yui-ac-bd,
#notifyBubbleContainer .messageContent,
#notifyBubbleContainer .notify_item_link {
  background: none !important; }

.hud-item.avatarName,
.complex-sublist li,
#notifyBubbleContainer strong {
  color: transparent !important;
  font-size: 0px !important; }

body.time-dawn,
body.time-day,
body.time-dusk,
body.time-night {
  padding-top: 25px !important; }

#gaia_header #gaia_menu_bar,
#gaia_header #menu_search {
  margin-top: -50px !important;
  -moz-transition: all .1s ease-in;
  -moz-transition-delay: .5s;
  -webkit-transition: all .1s ease-in;
  -webkit-transition-delay: .5s; }

#gaia_header:hover #gaia_menu_bar,
#gaia_header:hover #menu_search {
  margin-top: 0 !important;
  -moz-transition: all .1s ease-out;
  -webkit-transition: all .1s ease-out; }

.main_panel .panel-links > li > a,
.main_panel .gaia_menuitem_zomg,
.main_panel .gaia_menuitem_monstergalaxy,
.main_panel .gaia_menuitem_fishing,
.complex-sublist li > a,
#gsearchContainer .yui-ac-bd .name,
#notifyBubbleContainer .notify_item_link {
  display: block !important;
  font: 9pt/2.25 arial, helvetica, sans-serif !important;
  text-decoration: none !important;
  text-indent: 15px !important; }

.main_panel .panel-links > li > a:hover,
.main_panel .gaia_menuitem_zomg:hover,
.main_panel .gaia_menuitem_monstergalaxy:hover,
.main_panel .gaia_menuitem_fishing:hover,
.complex-sublist li > a:hover,
#gsearchContainer .yui-ac-bd .name:hover,
#notifyBubbleContainer .notify_item_link:hover {
  border-style: solid !important;
  border-width: 0 0 0 3px !important;
  text-indent: 12px !important; }

.hud-account,
.hud-stats {
  position: fixed !important;
  top: 0 !important;
  z-index: 999 !important; }

.hud-account {
  height: 25px !important; }

.hud-account {
  left: 0 !important;
  padding-left: 12px !important; }

.hud-account .hud-item-list {
  border: none !important;
  float: left !important;
  padding-top: 3px !important; }

.hud-item.avatarName {
  margin-right: 5px !important;
  min-width: 0 !important; }

.hud-item.avatarName span[style*="bold"] {
  font-size: 8pt !important; }

.hud-account .hud-item-list a {
  text-decoration: none !important; }

.hud-stats {
  margin-right: 16px !important;
  right: 0px !important; }

.hud-stats .hud-item-list {
  float: right !important;
  position: relative !important;
  top: -3px !important; }

.hud-stats .hud-item .hud-sprite {
  font-size: 8pt !important;
  margin-left: 15px !important;
  text-decoration: none !important; }

#gold_amt:after {
  content: " Gold"; }

#cash_amt:after {
  content: " Cash"; }

#achievements-points:after {
  content: " Points"; }

#gold_amt:after,
#cash_amt:after,
#achievements-points:after {
  font-weight: normal !important; }

#gaia_menu_bar {
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.2);
  height: 50px !important;
  left: 0px !important;
  position: fixed !important;
  top: 25px !important;
  z-index: 998 !important; }

#newmenu,
.megamenu-sections {
  height: 100% !important; }

.megamenu-no-panel,
.megamenu-standard-menu {
  height: 100% !important;
  width: 109px !important; }

.megamenu-section-trigger {
  height: 100% !important;
  font: 13pt/50px arial, helvetica, sans-serif !important;
  text-align: left !important;
  text-decoration: none !important;
  text-indent: 15px !important; }

.main_panel {
  width: 225px !important;
  border-style: solid !important;
  border-width: 3px 0 0 0 !important; }

.panel-left,
.panel-right,
.complex-sublist {
  float: none !important;
  width: 100% !important; }

li#menu_search {
  height: 50px !important;
  margin-right: 15px !important;
  position: fixed !important;
  right: 0px !important;
  top: 25px !important;
  width: 260px !important; }

#gsearch_ac,
#gsearch_ac.inactive,
#gsearch_form {
  width: 100% !important; }

#gsearch_form {
  padding-top: 11px !important; }

#gsearch_ac_input,
.inactive #gsearch_ac_input {
  border-style: solid !important;
  border-width: 1px !important;
  font: 11pt arial, helvetica, sans-serif italic !important;
  height: 25px !important;
  text-indent: 5px !important;
  width: 188px !important; }

#gsearch_ac_input {
  font: 11pt arial, helvetica, sans-serif !important; }

#gsearch_submit_container,
.inactive #gsearch_submit_container {
  display: block !important; }

#gsearch_ac .ta_submit,
#gsearch_ac .ta_submit_menu {
  cursor: pointer !important;
  height: 27px !important;
  text-indent: 0 !important; }

#gsearch_ac .ta_submit {
  border-style: dotted !important;
  border-width: 0 1px 0 0 !important;
  font: 8pt arial, helvetica, sans-serif !important;
  width: 49px !important; }

#gsearch_ac .ta_submit_menu {
  width: 21px !important; }

#gsearch_ac .ta_submit_menu:before {
  border-color: white transparent transparent !important;
  border-style: solid !important;
  border-width: 5px !important;
  content: "" !important;
  display: block !important;
  height: 0 !important;
  margin: 11px 0 0 5px !important;
  width: 0 !important; }

#gsearch_submit_menu {
  font-size: 9pt !important;
  top: 38px !important;
  width: 71px !important; }

#gsearch_submit_menu a {
  text-decoration: none !important; }

#gsearch_ac_input,
.inactive #gsearch_ac_input {
  background-color: white !important;
  background-position: 9999px !important;
  background-repeat: no-repeat !important; }

#gsearch_ac_submit.ta_ic.ta_submit {
  background-color: #444444 !important;
  background-position: 9999px !important; }

#gsearchContainer .yui-ac-content,
#gsearchContainer .yui-ac-content .yui-ac-bd,
#gsearchContainer .yui-ac-bd ul {
  width: 100% !important; }

#gsearchContainer .yui-ac-bd h3 {
  font-size: 9pt !important;
  padding: 5px 15px !important; }

#gsearchContainer .name {
  height: auto !important; }

#gsearchContainer .yui-ac-bd .yui-ac-highlight {
  cursor: pointer !important; }

#notifyBubbleContainer {
  border-style: solid !important;
  border-width: 1px !important;
  bottom: 30px !important;
  left: auto !important;
  overflow: hidden !important;
  position: fixed !important;
  right: 30px !important;
  top: auto !important;
  width: 200px !important; }

#notifyBubbleContainer strong:after {
  content: "Notifications";
  display: block !important;
  font: bold 9pt/2.25 arial, helvetica, sans-serif !important;
  text-indent: 15px !important; }

#notifyBubbleContainer .notify_item {
  height: auto !important; }

#notifyBubbleContainer .notify_item,
#notifyBubbleContainer .notify_item_link {
  width: 100% !important; }

#memberLoginChoices {
  z-index: 1000 !important; }

}
