css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.infoq.com/cn/") {
  
  #wrapper {
    background-image: none !important;
  }

  .all_topics, .header_banner_conferences, #topInfo, #footer, .bottomContent, .qcon_notice, .newsletter, #header .logo, #header .top_explain, #header img, #header .clear {
    display: none !important;
  }
  
  .search_cls {
    float: right !important;
    margin:10px 0 0 10px !important;
  }
  
  #header .menu{
    float: left !important;
    margin: 10px 0 0 0px !important;
    height: 40px !important;
  }

  ul.menu li a.main {
    height: 35px !important;
  }
  
  .top_submenu {
    top: auto !important;
  }
  
  ul.submenu li a {
    font-size: 15px !important;
    padding: 0px 6px !important;
  }
  
  #content{
    margin-top: 0 !important;
  }
  
  .title_canvas + .author_general{
    margin-top: -30px !important;
  }
  
  .title_canvas h1{
    margin-top: -30px !important;
  }
  
  .submenu{
    padding: 5px !important;
  }
  
  .breadcrumbs{
    padding-left: 10px !important;
  }
}
