css:
@-moz-document domain("welinux.ru"), url-prefix("http://welinux.ru/"){
/*font-family:"your font" !important;*/
html,body{
margin-top: 1px !important;
background-color:#e0e0e0 !important;
}
a{color:#dc7c00;}
*{
border:none!important; 
background:#e0e0e0 !important;
}

.mf{
height: 30px;
background-image: none !important;
background:#e0e0e0 !important;
border-top: 1px solid #000 !important;
border-bottom: 1px solid #000 !important;
}

.code{
background: #dbdbdb !important;
padding: 0px !important;
border: 1px solid #dc7c00 !important;
margin-bottom: 0em;
font-family: monospace;
overflow: auto;
width: 100% !important;
}
.highlight pre{
padding: 0px !important;
width: 100% !important;
}

.highlight{
margin-left: 15px !important;
overflow-x: visible !important;
width: 20px !important;
}

.ln{
background:#e0e0e0 !important;
border: 1px solid #000 !important;
}
table .fst_top{
overflow: none !important;
}
.top{
overflow: none !important;
}
.menu{
font-size: 1,0em!important;
height:18px!important;
z-index:10 !important;
left: 0%;
width: 100%;
top: 0px;
margin-top: 0px !important;
background:#e0e0e0!important;
}
.digg_pages_disabled {
color:#000000!important;
}
iframe{
display: none !important;
}
.advert, .search, #bottom,a[href='http://www.liveinternet.ru/click'], a[href="http://feeds.feedburner.com/welinux"] {
display: none !important;
}
#update_button_holder{display: none !important;}

.menu ol li
{
padding-top: 0px;
height: 1em  !important;
background: none !important;
background-image: none !important;
}
.menu ol li.current
{
background:#e0e0e0 !important;
}
.menu #user_setting
{
line-height: 19px;
height: 19px !important;
}
#user_setting .right
{
background: #c9c9c9;
}
#bottom
{
border-top: 1px solid #000!important;
border-bottom: 1px solid #000!important;
height: 19px !important;
background:#e0e0e0 !important;
}
.mini_av, .user_tag img {
max-height: 50px;
}
}
