css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document url-prefix(https://www.google.com/voice), url-prefix(https://www.google.com/voice/b/0#inbox){

{
	outline: none !important;
}

a {
	display: inline-block;
	padding:  2px 4px;
	background:  #eee;
	-webkit-border-radius: 3px;
	text-decoration: none;
	color: #435385 !important;
}

a:hover {
	background: #dee3f1 !important;
	color: #435385 !important;
	text-decoration: none !important;
}

.gc-message-bg-m div, .gc-message-bg-g div {
	text-decoration: none !important;
	color: #344d93 !important;
}

#gbar {
	display: none !important;
}

#gbs, .gbm {
        background: #aaa !important;
        border-color: #fff !important;
}


#gc-gaia-bar a {
        background: none !important;
        color: #eee !important;
	font-weight: bold !important;
	font-style: normal !important;
	text-shadow: rgba(0,0,0,0.5) 0 1px 2px !important;
	text-decoration: none  !important;
	display: inline-block !important;
        background: none !important;
}

#gc-gaia-bar .gbh {
	display: none !important;
}

#gc-gaia-bar .gc-header-right {
	position: absolute !important;
	right: 0px !important;
	color: rgba(255,255,255,0.2) !important;
	text-shadow: #000000 1px 0 0 !important;
	padding-top: 10px !important;
}

#gc-header {
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(0, rgb(166,166,166)),
	    color-stop(1, rgb(97,97,97))
	) !important;
	border-top: 1px solid #4b4b4b !important;
	margin-left:  170px !important;
}

.gc-header-logo {
	display: none !important;
}

.gc-search {
	margin: 6px 8px !important;
	padding: 0 !important;
	width: 223px !important;
	-webkit-box-shadow: rgba(0,0,0,0.6) 0 1px 3px;
	-webkit-border-radius: 5px;
	z-index: 7 !important;
	position: relative !important;
}

.gc-header-left {
	padding: 0 !important;
}

#gc-search-input {
	margin: 0 !important;
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(177,177,177)),
	    color-stop(0.3, rgb(235,235,235)),
	    color-stop(0, rgb(285,285,285))
	) !important;
	border: 0px !important;
	height: 19px !important;
	-webkit-border-radius: 5px;
	z-index: 6 !important;
	position: relative !important;
	outline: none !important;
	width: 220px !important;
}

#gc-search-btn {
	display: none !important;
}

#gc-search-btn div.goog-inline-block.goog-button-base-outer-box {
	border: 1px solid #9d9d9d !important;
	border-top: 0px !important;
	-webkit-border-top-right-radius: 5px;
	-webkit-border-bottom-right-radius: 5px;
}

#gc-search-btn div.goog-inline-block.goog-button-base-outer-box div.goog-inline-block.goog-button-base-inner-box div.goog-button-base-pos div.goog-button-base-content {
	height: 23px !important;
	padding-top: 2px !important;
	padding-left: 15px !important;
	padding-right: 15px !important;
	
}

#gc-search-btn {
	left: -8px !important;
	position: relative !important;
	margin: 0 !important;
	z-index: 5 !important;
	border-right: 0px !important;
	text-shadow: #fff 0 1px 0 !important;
}

#gc-search-btn div {
	-webkit-border-top-right-radius: 5px;
	-webkit-border-bottom-right-radius: 5px;
	border-right: #959595 !important;
}

.gc-inbox-sidebar-header,.gc-inbox-view-header {
	background-color:#bcf !important;
	border-top: 1px solid #def !important;
}

.gc-inbox-sidebar-header {
        position: relative !important;
        margin-top: 40px !important;
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgba(255,255,255,1)),
	    color-stop(0.5, #e3e3e3),
	    color-stop(0.5, #eee),
	    color-stop(0, #e3e3e3)
	) !important;
	padding: 0px !important;
	height: 26px !important;
	border-bottom: 1px solid #ccc !important;
	
	width:  169px !important;
	z-index: 90 !important;
	bottom: 0px !important;
	border-top: 1px solid #62687b !important;
}

.gc-inbox-sidebar-header .goog-button-base > * {
	border: 0px !important;
}

.gc-inbox-sidebar-header .goog-button-base-content {
	height: 22px !important;
	padding: 2px 5px !important;
}

.gc-inbox-sidebar-header .goog-button-base-inner-box {
	border-right: 1px solid #aaa !important;
}

.gc-inbox-view-header {
	padding-left: 5px !important;
}

.goog-splitpane-handle {
	border-top: 1px solid #def !important;
}

.gc-inbox-search-banner {
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(177,255,177)),
	    color-stop(0.48, rgb(177,255,177)),
	    color-stop(0.48, rgb(207,255,207)),
	    color-stop(0, rgb(157,235,157))
	);
	border-top: 1px solid #fff !important;
	z-index: 70 !important;
}

#gc-view-loading table tbody tr td.gc-rounded-mm.gc-rounded-bg.gc-status-text {
	-webkit-border-radius: 20px;
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(0, rgba(0,0,0,0.9)),
	    color-stop(1, rgba(100,100,100,1))
	) !important;
	padding: 6px 14px !important;
	margin-bottom: 0px !important;
	text-shadow: #000 0 1px 1px !important;
	color: #fff !important;
	font-weight: normal !important;
	font-style: normal !important;
	z-index:  60 !important;
	position: absolute !important;
	top:  100px !important;
	font-size: 20px !important;
	-webkit-box-shadow: #000 0 1px 4px;
}

a#gc-header-did-link.gc-under , #gc-gaia-bar div.gc-header-right b, #gc-gaia-bar div.gc-header-right a {
	color: #eee !important;
	font-weight: bold !important;
	font-style: normal !important;
	text-shadow: rgba(0,0,0,0.5) 0 1px 2px !important;
	text-decoration: none !important;
	display: inline-block !important;
        background: none !important;
}

#gc-gaia-bar div.gc-header-right a {
	background: rgba(255,255,255,0.08) !important;
	padding: 2px 3px !important;
	-webkit-border-radius: 3px;
}

#gc-gaia-bar div.gc-header-right a:hover {
	color: #fff !important;
	background: rgba(255,255,255,0.28) !important;
}

#gc-view-loading td {
	background: none !important;
}

.gc-contact-ac {
	-webkit-box-shadow: rgba(0,0,0,0.3) 0 2px 4px;
	border: 1px solid #fff !important;
	-webkit-border-bottom-left-radius: 5px;
	-webkit-border-bottom-right-radius: 5px;
	-webkit-border-top-right-radius: 5px;
	margin-left: 5px !important;
	width: 278px !important;
	z-index: 6 !important;
	background: #fff !important;
}

.gc-contact-ac > * {
	-webkit-border-radius: 3px;
	color: #888 !important;
}

.gc-contact-ac b.ac-highlighted {
	-webkit-border-radius: 5px;
	color: #316688 !important;
}

.gc-contact-ac .active {
	background: #daeaf3 !important;
}

.gc-contact-ac .ac-row {
	font-size: 14px !important;
	text-shadow: #fff 0 1px 1px !important;
	padding: 4px 5px !important;
	margin: 2px !important;	
}

.gc-contact-card2, .gc-contact-card-data {
	z-index: 6 !important;
	-webkit-border-bottom-left-radius: 5px;
	-webkit-border-bottom-right-radius: 5px;
	-webkit-border-top-right-radius: 5px;
	border: none;
	width: 270px !important;
	-webkit-box-shadow: rgba(0,0,0,0.3) 0 2px 4px;
}

#gc-sidebar-invites {display: none !important;}



#gc-contact-card2.goog-shadow {
	width: 278px !important;
}

span.gc-contact-card-name {
	font-size: 14px !important;	
	color: #316688 !important;
}

.gc-contact-card-data-left {
	width: 200px !important;
	margin-right: 0px !important;
}

.gc-contact-card-data-right {
	border-bottom-width: 0px !important;
	padding-bottom: 19px !important;
}

.gc-contact-card2 {
	margin-left: 5px !important;
	text-shadow: #fff 0 1px 1px !important;
}

div.gc-contact-card-data-right img {
	-webkit-box-reflect: below 1px -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(0.7, transparent), to(rgba(255,255,255,0.3))) !important;
	-webkit-border-radius: 3px !important;
}

div.gc-contact-card-sub {
	color: #888 !important;
}

.gc-contact-card-sub2 {
	color: #51884d !important;
	display: inline-block !important;
	text-indent: -4px !important;
	padding-right: 3px !important;
	padding-bottom: 1px !important;
	overflow: hidden !important;
	-webkit-border-radius: 3px !important;
	background: #dbf3da !important;
	font-size: 11px !important;
	margin-left: 5px !important;
}

.gc-contact-card2 .goog-shadow-0, .gc-contact-card2 .goog-shadow-1, .gc-contact-card2 .goog-shadow-2, .gc-contact-card2 .goog-shadow-3, .gc-contact-card2 .goog-shadow-4, .gc-contact-card2 .goog-shadow-5, .gc-contact-card2 .goog-shadow-6, .gc-contact-card2 .goog-shadow-7 {
	display: none !important;
}

.goog-tab {
	color: #627093 !important;
	text-decoration: none !important;
	padding-left: 8px !important;
	padding-right: 8px !important;
}

.goog-option.gc-inbox-sidebar-item.goog-menuitem.goog-option-selected {
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(127,167,215)),
	    color-stop(0.5, rgb(87,117,175)),
	    color-stop(0.5, rgb(107,147,185)),
	    color-stop(0, rgb(107,137,175))
	) !important;
	text-shadow: rgba(46,99,144,0.63) 0 -1px 0 !important;
}



.goog-option.gc-inbox-sidebar-item.goog-menuitem-highlight {
	background: #e2e5f1 !important;
}

.gc-inbox-sidebar-menu,.gc-inbox-sidebar-types-header,.gc-inbox-sidebar-types-menu,.gc-sidebar-contact-menu {
	background: #e2e5f1 !important;
	-webkit-box-shadow: none !important
}

.goog-menu {
	-webkit-box-shadow: rgba(0,0,0,0.4) 0 1px 4px;
}

.gc-inbox-sidebar-sections {
/*        position: relative !important;
        margin-top: 40px !important;    */
	border-bottom-color: #cad0e7 !important;
}

#gc-sidebar-invite-header {
	border-top-width: 0px !important;
}

#gc-sidebar-invites a {
	color: #7c839b !important;
	display:  block !important;
	text-align: center !important;
	margin-right: 8px !important;
	padding:  2px 5px !important;
	text-decoration: none !important;
	-webkit-border-radius: 3px !important;
	background: #cbd1e5 !important;
}

div.gc-bubble, div.gc-bubble > td, .gc-bubble-layout tr td div.gc-bgimg {
	background: none !important;
	border:  0px !important;
}


.goog-bubble-close-button {
	background-image: url(http://grab.by/1pYQ) !important;
	background-color: transparent !important;
	width: 29px !important;
	height: 28px !important;
	right: 1px !important;
	top: -9px !important;
	display:  block !important;
	
}

td.gc-bubble-mc {
	background: rgba(255,255,255.93) !important;
	-webkit-border-radius: 5px;
	padding:  10px !important;
	display:  block !important;
	color:  #aaa !important;
	text-shadow: #fff 0 1px 1px;
	-webkit-box-shadow: #000 0 2px 4px;
	border: 1px solid rgba(255,255,255,0.62) !important;
}

td.gc-bubble-mc div.goog-flat-menu-button {
	text-shadow: none !important;
}

td.gc-bubble-mc .goog-button div {
	background: none !important;
	border:  0px !important;
	color:  #fff !important;
}

td.gc-bubble-mc .goog-button {
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(130,130,130)),
	    color-stop(0.5, rgb(90,90,90)),
	    color-stop(0.5, rgb(120,120,120)),
	    color-stop(0, rgb(60,60,60))
	) !important;
	-webkit-border-radius: 3px;
}

.gc-bubble-mc img.gc-help-call.gc-help-button {
	display:  none !important;
}

div.gc-bgimg.gc-bubble-callout {
	display:  none !important;
}

#gc-inbox-sidebar-invites-left {
	display: none !important;	
}

#gc-sidebar-balance {
	display: block !important;
	position: fixed !important;	
	bottom: 28px !important;
	z-index: 50 !important;
	left: 0px !important;
	width: 169px !important;
	border-top: 1px solid #737990 !important;
	padding-top: 0px !important;
	background-color: #adcd6d !important;
}

#gc-sidebar-balance div {
	padding-left:  0px !important;
}

#gc-sidebar-billing-actions {
	margin-left: 8px !important;
}

#gc-sidebar-billing-actions a {
	background-color: #adcd6d !important;
	-webkit-border-radius: 0px !important;
	display: inline !important;
}

#gc-sidebar-balance > * {
	background-color: #adcd6d !important
}

div#gc-sidebar-balance-header.gc-inbox-sidebar-types-header.gc-sidebar-contacts-header {
	display: none !important;
}

#gc-sidebar-currency {
	font-size: 20px !important;
	text-shadow: #c6e980 0 1px 1px;
	color: #445411 !important;
	background: #97a3b7 !important;
	display: block !important;
	margin: 0px !important;
	padding: 8px 8px !important;
	background:  -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(0, rgb(111,138,21)),
	    color-stop(0.5, rgb(161,194,64)),
	    color-stop(0.5, rgb(179,214,102)),
	    color-stop(1, rgb(221,255,158))
	) !important;
	-webkit-border-radius: 0px !important;
	border-bottom: 1px solid #52740e;
	border-top: 1px solid #fff;
}

#gc-sidebar-currency:hover {
	background:  -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(0, rgb(101,128,11)),
	    color-stop(0.5, rgb(151,184,54)),
	    color-stop(0.5, rgb(169,204,92)),
	    color-stop(1, rgb(211,245,148))
	) !important;
	text-decoration: none !important;
}

div#gc-sidebar-billing-actions > * {
	color: #647835 !important;
	border-color: #a2b38f !important;
}

.goog-option.gc-inbox-sidebar-item {
	background: #e2e5f1 !important;
}

td.goog-splitpane-first-container {
	display: block !important;
	position: absolute !important;
	top: 0px !important;
	border-right: 1px solid #8e9097 !important;
	height: 100% !important;
	overflow: visible !important;
	background-color: #e2e5f1 !important;
	-webkit-box-shadow: #000 -1px 0px 5px !important;
	z-index: 20 !important;
	width:  170px !important;
}

td.goog-splitpane-first-container > * {
	background: #e2e5f1 !important;
}

td.goog-splitpane-second-container {
	margin-left: 700px !important;
	position: relative !important;
}

td.goog-splitpane-handle {
	display: none !important;
}


.goog-tab-selected {
	padding-left: 7px !important;
	padding-right: 7px !important;
	color: #000 !important;
	-webkit-border-top-left-radius: 5px;
	-webkit-border-top-right-radius: 5px;
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(255,255,255)),
	    color-stop(0.94, rgb(227,227,255)),
	    color-stop(0, rgb(255,255,255))
	) !important;
	font-weight: normal !important;
	font-style: normal !important;
}

#gc-view-title {
	text-shadow: #fff 0 1px 1px !important;
	color: #6378b9 !important;
	font-weight: normal !important;
	font-style: normal !important;
	margin-top: 6px !important;
	padding: 3px 8px!important;
	background: -webkit-gradient(
	    linear,
	    left bottom,
	    left top,
	    color-stop(1, rgb(255,255,255)),
	    color-stop(0, rgb(235,239,249))
	) !important;
	-webkit-border-top-left-radius: 7px;
	-webkit-border-top-right-radius: 7px;
}

#gc-inbox-actions-header .goog-button-base div {
	background: none !important;
	border:  0px !important;
	color: #8ea2da !important;
}

#gc-inbox-actions-header .goog-button-base {
	text-decoration: none !important;
	padding:  1px 1px !important;
	background-color: #dce5ff !important;
	color: #8ea2da !important;
	-webkit-border-radius: 3px !important;
	font-size:  11px !important;
	text-shadow: rgba(255,255,255,0.87) 0 1px 0 !important;
	margin-right: 2px !important;
	border: 1px solid #a4b9f6 !important;
}

a#gc-inbox-refresh.gc-inbox-refresh {
	text-decoration: none !important;
	display: inline-block !important;
	padding:  4px 5px !important;
	background-color: #c8e7dd !important;
	color: #789982 !important;
	-webkit-border-radius: 3px !important;
	font-size:  11px !important;
	text-shadow: rgba(255,255,255,0.87) 0 1px 0 !important;
	border: 1px solid #a4b9f6 !important;
}

#gc-view-notification {
	z-index: 99 !important;
	top:  70px !important;
}

.modal-dialog-bg {
	z-index: 140 !important;
	background-repeat: repeat;
}

.gc-contact-card2 .gc-contact-card-data .gc-contact-card-sub a {
	color: #557588 !important;
	font-size: 11px !important;
	text-decoration: none !important;
	display: inline-block !important;
	padding: 0px 4px 1px 4px !important;
	background: #daeaf3 !important;
	-webkit-border-radius: 3px !important;
}

.gc-contact-card-title a {
	font-size: 11px !important;
	text-decoration: none !important;
	display: inline-block !important;
	padding: 0px 4px 1px 4px !important;
	background-color: #efefef !important;
	color: #a1a1a1 !important;
	-webkit-border-radius: 3px !important;
}

.gc-user-tip {
        display: none !important;
}}
