css:
@-moz-document url-prefix("http://www.lds.org/scriptures") {

#beta-feedback, #saveAndShareToolbar, #context-bar {visibility: hidden}
#platform-canvas-content {background-color: #63625e !important }
#content { background: #333 !important }
#primary { background: #000 !important; color: #777 !important; width: 600px !important }
#secondary { background: inherit !important; margin-top: 0 !important; width: 180px !important }
#secondary #bottom-shadow { padding: 30px 15px !important; font-size: 9pt }
#top-shadow, #bottom-shadow {background: inherit !important }
#bottom-gradient {background: inherit !important; padding: 0px !important }

h2 { color: #777 !important }
.narrow .two-col #primary { padding-bottom: 5px }
.summary p, .intro {color: #777 !important }
.verses p { color: #555; font: 16pt Georgia !important }
.search-form {width: 150px !important }
.search-field { width: 120px !important }
.prev-next { background: none !important; border: none !important; float: right !important }
.prev-next li + li { border-color: #666 }
.prev-next a { padding: 4px 10px; color: #486fae }
.prev-next a:hover { color: #3b4b6d }

}
