css:
namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fanfiction.net") {


/*Remove those stupid ads*/
#google_ads_div_ff_safe_banner_728x90_ad_container,
#google_ads_div_ff_safe_banner_728x90,
#google_ads_div_ff_rectangle_300x250_ad_container,
#google_ads_div_ff_rectangle_300x250,
#google_ads_div_ff_tower_160x600_ad_container,
#google_ads_div_ff_tower_160x600,
#google_ads_div_ff_text_160x600_ad_container,
#google_ads_div_ff_text_160x600,
#adcontainer1,
ispan,
center div { display: none !important;}

/*Change the Colors*/
body, table, tbody, tr, td, div.panel_normal { background-color: #000 !important; color: #ddd !important;}
.zmenu, .zui, select, input[type="text"], input[type="password"], textarea { background-color: gray !important; color: white !important;}
input[type="text"]::-webkit-input-placeholder, input[type="password"]::-webkit-input-placeholder { color: white;}
input[type="text"]:-moz-placeholder, input[type="password"]:-moz-placeholder { color: white;}
a:link { color: white;}
a:visited { color: gray;}
a.reviews:link { color: #ff8040;}
a.reviews:visited { color: #ff8040;}
a[href="http://www.fanfiction.net/"], a.menu-link, span[id|="menu-"] a { color: white;}
.z-high { background-color: #292929;}
.z-padtop2 { color: #777;}
#storytextp {background-color: #333333;}
#storytext {color: #ddd;}

}
