css:
@-moz-document url(chrome://sage/content/sidebar.xul) {

/* Cleaning up Sage for mac v.06 */
/* The CSS is kind of unstructued but I can't be bothered to organise it */
/* -------------- */
/* Love, Brendan */


#sagePanel {
        background:#DDD !important;
        border-right:none !important;
}

#feedTitleBar{
	display:none!important;
}
#feedListToolbar {
        border:none !important;
        background:transparent !important;
        margin: 0 0px 0 5px;
}

#statusBar{
	display:none!important;
}

#bookmarks-view {
        border-top:0 !important;
        font-size:10px !important;
}

#bookmarksTreeBox{
        background:transparent;
        font-family:'Trebuchet MS' !important;
	font-size:11px !important;
        padding-top:3px;
        padding-bottom:0px;
        margin: 0 0px 0 5px;
}
#sage-splitter{
        background:transparent !important;
        height:1px !important;
        max-height:1px !important;
        max-height:1px !important;
        /* border-top:1px dotted #a2a2a2 !important; */
        /* border-bottom:1px dotted #a2a2a2 !important; */
        margin:0px 0 0px 0;
}
#rssItemListBox{
        background:transparent !important;
        font-family:'Trebuchet MS' !important;
	font-size:11px !important;
        font-weight:normal;
        margin: 0 0px 0px 5px !important;

}
#rssItemListBox > listitem {
	font-weight:normal !important;
        color:#000 !important;
        margin-right:0px !important;

}
#rssItemListBox > listitem[selected="true"] {
	background:#BBB !important;
        color:#000 !important;
        font-weight:bold !important;

}
#rssItemListBox > listitem[visited="true"] {
	color:#222 !important;

}

/* Sidebar and Library list selected inactive */
#bookmarks-view treechildren::-moz-tree-row(selected), 
#historyTree treechildren::-moz-tree-row(selected),
#placesList treechildren::-moz-tree-row(selected) {
  background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAATCAIAAADeafBOAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdJREFUeNpi3rFjB9Pfv3/B+N+/f0z///8H0zAxXOIwMRgfyAYIMAAsrjfVMVwyqQAAAABJRU5ErkJggg==")!important;
  background-color: #A2A2A2 !important;  
  border-top: 1px solid #A2A2A2 !important;
}
/* Sidebar and Library list selected */
#bookmarks-view treechildren::-moz-tree-row(selected,focus),
#historyTree treechildren::-moz-tree-row(selected,focus),
#placesList treechildren::-moz-tree-row(selected,focus) {
  background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAATCAIAAADeafBOAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAC5JREFUeNpizMzMZPrz5w/T79+/wfS/f//gbBj++/cvWBxZDMRHFvv//z9AgAEA3Qs2p3+6EJEAAAAASUVORK5CYII=")!important;
  background-color: #4B4B4B !important;
  border-top: 1px solid #4B4B4B !important;
}
#bookmarks-view treechildren::-moz-tree-image {
  list-style-image: url(data:image/png;base64,) !important;
  -moz-image-region: auto !important;
  height:10px !important;
  width:10px !important;
  margin: 0px 3px 0 -18px !important; 
}
#bookmarks-view treechildren::-moz-tree-image(container) {
  list-style-image: url(data:image/png;base64,) !important;
  -moz-image-region: rect(0px 14px 14px 0px) !important;
  height:13px !important;
  width:13px !important;
  margin: 0px 5px 0 -20px !important; 
}
#bookmarks-view treechildren::-moz-tree-cell-text(container) 
{
	font-weight: bold !important;
        font-size:11px !important;
        margin:1px 0 0 0 !important;
}

}

#sidebar-splitter { -moz-border-end-color: #a9a9a9 !important; }

sidebarheader { display: none !important; }
sidebar { border-right:0px !important; }
