@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
#main-window #nav-bar {position: fixed; background: none !important; -moz-transition: 0s ease;  opacity: 0;-moz-transition-delay: 0.5s; width:1700px !important}
#urlbar {width:1200px !important}
#searchbar {width:300px !important}
#nav-bar:hover{opacity: 1 !important;margin-top: 27px}
#nav-bar:not(:hover){-moz-transition-delay: 5s !important; margin-top: -32px}
