css:
@-moz-document domain('shoujoai.com') {
	/* Font */
	body, .main, h2, h3, td, a, .nav a, .cb a, .footer, pre {
		font-family: Georgia, serif !important;
}
body {
	max-width: 1024px;
	margin: 10px auto;
	overflow-y: scroll;
}
a {
	text-decoration: none;
}
a:hover {
	text-decoration: underline !important;
}
h1 {
	text-align: center;
}
h2 {
	color: #fb81d8;
	font-size: 2.5em !important;
	text-shadow: 1px 1px 0 #fff;
	margin: 0;
}
h2 + p, table + p {
	display: none;
}
h3 {
	font-size: 1.5em !important;
}
table img {
	outline: dotted 2px #fb81d8;
}
td {
	vertical-align: top;
}

/* Navigation */
.nav {
	left: 25%;
}
.nav a {
	font-style: normal;
	font-weight: normal;
}
.nav a:before {
	content: "\2764  ";
}
.frame {
	cursor: pointer;
}

/* Main Content */
.main {
	top: 0px;
	padding: 0;
	font-size: 16px !important;
}
.main p {
	max-width: 960px;
}

/* Header */
.main .header ,.main .header img {
	text-align: left !important;
}

/* Sidebar */
.cb {
	margin: 0;
}
.cb a {
	font-size: .8em;
}
/*Remove sidebar top and bottom border divs*/
.bt, .bb {
	display: none;
}
/*Remove sidebar left and right image borders */
.i1, .i2 {
	background: none !important;
}

/* Sidebar blurry borders with box shadow */
.i3 {
	box-shadow: #FEE9F8 0 0 10px 10px;
}

hr {
	margin-top: 2%;
}
}
