css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reader.livedoor.com") {
        /*　記事の書式の変更　*/
         body{ font-size : 12px !important; line-height : 1.6 !important; }
        /*　上から順に変更していきます　*/

        /*　1.header部分を消す　*/
         #header{ display : none !important; }
        /*　2.次はmenu部分へ移ります　*/

        /*　logo部分を消す　*/
         #reader_logo{ display : none !important; }
         /* 邪魔なボタンやマイフィードをデフォルトで畳む */
         #up_button{display : none !important; }
         #down_button{display : none !important; }
         #myfeed{display : none !important; }
         #control_buttons{left :3px!important;}

        /*　カワイイあのキャラクターを移動させます（名前はなんていうんだ？）　*/
         #message_box{ position : absolute !important;margin-top:1px!important; top : 0px !important; left: 200px !important; z-index: 2 !important; }
        /*　menu部分のリンクの処理　*/
         #my_menu{ position : absolute !important; left : 0px !important; top : 1px !important; overflow : hidden !important; z-index : 3 !important; font-size : 16px !important; line-height : 22px !important; padding: 0 !important; height: 0px !important; width: 100%; color: #000000 !important; background-image: url(http://image.livedoor.com/img/top/11/title_bg_normal.gif) !important; border-bottom: 1px solid #999 !important ; } #my_menu b { margin : 0 0 0 0 !important; color : #333 !important; } #my_menu a { margin : 0 5px !important; } #my_menu:hover { height : auto !important; overflow : visible !important; }
        /*　未読カウンターを移動させます　*/
         #total_unread_count{ position : absolute !important; top : 5px !important; right: 80px !important; z-index: 2 !important; }
        /*　移動先のスペースを空けます　*/
         #ads_top{ display : none !important; }
        /*　3.Control部分とContainer部分を移動させます　*/
         #control{ position : absolute !important; top : 2px !important; } #container{ position : absolute !important; top : 30px !important; padding-bottom: 0 !important; }
        /*　4.フッター部分を消します　*/
         #footer,#ads_bottom { display : none !important; }
        /*　おまけ1：フラットカテゴリーが見にくいのでちょっと調整　*/
         .flat .treeitem { height : auto !important; }
        /*　おまけ2：引用部分も見にくいので、枠で囲んでみる　*/
         blockquote { background-color : #F8F8F8 !important; margin : 9px !important; padding : 9px !important; border : 2px solid #000000 !important; }
        /*　スポンサーフィード　*/
         .adfeeds_body {display: none;}
         .r25_box,.adsWrapper {display: none;}
        /* エラーウィンドウ */
        #error_window{ display: none!important;}
}
