css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tabmodalprompt .mainContainer {
	-moz-appearance: none !important;
	background-color: rgba(179,230,255,.35) !important;
	background-image: -moz-linear-gradient(rgba(250,253,255,.9), rgba(250,253,255,.75) 20px, rgba(250,253,255,.70) 39px, rgba(250,253,255,0) 41px, rgba(250,253,255,0)) !important;
	border: 0 !important;
	border-radius: 6px !important;
	box-shadow: 0 0 0 1px rgba(255,255,255,.65) inset,
				0 1px 0 rgba(255,255,255,.3) inset,
				0 1px 8px 2px rgba(0,0,0,.65) !important;
	margin: 5px !important}

tabmodalprompt .topContainer {
	background-color: rgb(253,253,253) !important;
	background-clip: padding-box !important;
	border: 2px solid !important;
	border-bottom: 0 !important;
	-moz-border-top-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	-moz-border-left-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	-moz-border-right-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	border-radius: 2px 2px 0 0 !important;
	margin: 4px !important;
	margin-bottom: 0 !important;
	padding: 16px !important}

tabmodalprompt .buttonContainer {
	background-color: #F1F5FB !important;
	background-image: -moz-linear-gradient(#CCD9EA 1px, #DAE1F0 2px, #E3EAF5 3px, #ECF0F8 4px, RGBa(255,255,255,0)) !important;
	background-clip: padding-box !important;
	border: 2px solid !important;
	border-top: 0 !important;
	-moz-border-left-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	-moz-border-bottom-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	-moz-border-right-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
	border-radius: 0 0 2px 2px !important;
	margin: 4px !important;
	margin-top: 0 !important;
	padding: 15px 20px !important}

tabmodalprompt .topContainer textbox {
	-moz-appearance: none !important;
	background: White padding-box !important;
	border: 1px solid !important;
	border-color: hsla(210,54%,20%,.25) hsla(210,54%,20%,.27) hsla(210,54%,20%,.3) !important;
	border-radius: 2px !important;
	min-height: 24px !important;
	padding: 3px 3px 2px !important}
tabmodalprompt .topContainer textbox[disabled="true"] {
	opacity: .6 !important}
tabmodalprompt .topContainer textbox:not([focused]):hover {
	border-color: hsla(210,54%,20%,.35) hsla(210,54%,20%,.37) hsla(210,54%,20%,.4) !important}
tabmodalprompt .topContainer textbox[focused="true"] {
	border-color: hsla(206,100%,60%,.65) hsla(206,100%,55%,.65) hsla(206,100%,50%,.65) !important}
