css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.xbox.com")url-prefix('http://forums.xbox.com') 
	

body {
background-color: #3d3d3d !important;
color: #a2a2a2 !important;
}
.fiji-full-post-container {
background-color: #3d3d3d !important;
}
.content-fragment-content {
background-color: #525151 !important;
}
.full-post {
background-color: #3d3d3d !important;
}
.post-content {
background-color: #3d3d3d !important;
color:ffffff!important;
}
.user-signature {
background-color: #3d3d3d !important;
}
.fiji-post-tags-container {
background-color: #3d3d3d !important;
}
.fiji-post-actions-cell {
background-color: #3d3d3d !important;
}
.layout-region-inner {
background-color: #3d3d3d !important;
}
.fiji-user-name-post-date {
background-color: #3d3d3d !important;
}
.fiji-post-author-details-table {
background-color: #3d3d3d !important;
}
.fiji-post-details-container td {
background-color: #3D3D3D !important;
}
a.add-post {
background-color: #525151 !important;
}
.navigation-item {
background: #525151 !important;
}
.layout-region.header {
background: #525151 !important;
}
.layout-region-inner {
background: #525151 !important;
}
.full-post .field-item {
background: #525151 !important;
}
.field-list {
background: #525151 !important;
}
.field-list textarea {
background: #3d3d3d!important;
}
.navigation-list.parent {
background: #3d3d3d!important;
border:0!important;
}
.post-tags input {
background:#3d3d3d!important;
}
.field-item-description p {
background:#3d3d3d!important;
color:#fff!important;
}
.user-defined-markup p {
color:#ffffff!important;
}
.user-defined-markup {
color:#ffffff!important;
}
#ShellHeaderContent {
background:#3d3d3d!important;
}
#ShellLinkArea {
background:#3d3d3d!important;
}
#BodyHeader {
background:#3d3d3d!important;
}
.fiji-header-fragment-outer.group-navigation {
background: none repeat scroll 0 0 #525151!important;
height: 30px!important;
}
.navigation-list.parent {
background: none repeat scroll 0 0 #525151!important;
height: 30px!important;
}
.layout-content {
background:#525151!important;
}
#bodycolumn {
background:#3d3d3d!important;
}
.XboxForumsHome {
background:none!important;
}
.heroBlog {
background-color: #525151!important;
}
.heroForum {
background-color: #525151!important;
}
.heroBlog h4, .heroForum h4 {
background:#525151!important;
}
#ShellHeaderContent, #ShellMeBarArea, #ShellMeBar {
background:#3d3d3d!important;
border-bottom: none!important;
border-top: 6px solid #3d3d3d!important;
}
#ShellLogo {
background-color:#3d3d3d!important;
background-image: url("http://z3.ifrm.com/326/148/0/p405863/xbforumlogo.png")!important;
}
#ShellHeaderContent, #ShellNavigationBar {
background-color:#3d3d3d!important;
}
#ShellNavigationBar ul, .NavigationElements li a {
background-color: #3d3d3d!important;
background-image:none!important;
}
#ShellHeaderContent, #ShellNavigationBar a, .CalloutNavigation, #ShellHeaderContent, #ShellNavigationBar a, .CalloutNavigation:visited {
background-color: #3d3d3d!important;
}
.content-fragment.with-header.no-wrapper .content-fragment-header, .content-fragment.with-header.no-wrapper-with-spacing .content-fragment-header {
background-color: #525151!important;
}
#ShellHeaderContent, #ShellMeBarArea, #HeadShotBox.SignedIn, div.avatarHeadshot {
background-color:#3d3d3d!important;
}
.content-fragment.thread-list .table-header .table-header-column {
background: #525151!important;
}
.content-fragment.thread-list .table-column.post-name {
background: #525151!important;
}
.content-fragment.thread-list .abbreviated-post.discussion {
background:#525151!important;
}
.content-fragment.thread-list .table-header-column.total-replies-sort, .content-fragment.thread-list .table-column.total-replies {
background: #525151!important;
}
.content-fragment.thread-list .table-column.post-status {
background:#525151!important;
}
.content-fragment.thread-list .filter {
background: #525151!important;
}
.content-fragment-page {
background:#3d3d3d!important;
}
.navigation-list {
padding: 0!important;
}
.content-fragment.thread-list .filter-option.selected {
background:#525151!important;
}
.forum-banner .application-banner .application-name {
background:#525151!important;
}
.content-fragment.conversation-list table.standard.fiji-standard {
background-color: #3D3D3D!important;
}

/* Resize nav text */
.breadcrumb-list .breadcrumb-item a {
font-size: 12px!important;
}

/* Remove spacing on forum topic page */
.content-fragment.thread-list .table-column {
padding: 0 0 0 10px!important;
}

/* Add space between nav menu and other dropdowns */
#ShellHeaderContent #ShellNavigationBar {
padding: 0 0 40px!important;
}

/* Make achievement title darker */
.AchievementInfo h3 {
color: #000!important;
}

/* Sig overflow */
.user-signature {
max-height:180px!important;
overflow:auto!important;
}
