css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://loadingreadyrun.com/forum/") {

body{
color: #000 !important;
background-color: #abf !important;
}

#background {
	background-color:#bcf !important;
}

#container{
background-color: #bcf !important;
color: #000 !important;
}

a:link{
color: #180094 !important;
}

a:active, a:visited {
	color: #180094 !important;
}

p.breadcrumbs, p.moderators, .nav{
color: #557 !important;
}

#navbar {
margin: 10px 10px !important;
background-color: #cdf !important;
}

#navbar ul li {
border-left: 10px #ccf solid !important;
}
#navbar ul li.last {
border-right:10px #ccf solid !important;
}

p.searchbar {
padding: 2px 0px !important;
}

th {
background-color: #9bf !important ;
}

.tablebg {
border: 3px #999 solid !important ;
}

.tablebg{
background-color: #cdf !important;
}

.quotetitle, .attachtitle {
background-color: #9bf !important ;
}

.copyright {
color: #000 !important ;
}

.spoiler {
color: #cdf !important ;
}

/* REMOVE THIS SECTION TO GET THE OLD FORUM HEADER IMAGE BACK */
h1 img {
background-image: url(http://i1239.photobucket.com/albums/ff512/SilPho_/LRR_forum_logo.gif) !important;
height: 0px !important;
padding: 63px 0px 0px 0px !important ;
}

/* REMOVE THIS SECTION TO RETAIN ADVERTISING SPACE */
#forum_container{
width:928px !important;
}

}
