css:
/*
Simple Design Extended Edition
Author: KRRX
*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("justin.tv") {



/*************************************************************
* CUSTOM ZONE */


/* ADS */
/* IN ADDITION USE ADBLOCK+ */
#ChanMedRectv2,
#DirectoryMedRectv2,
#DirectoryLeaderBottomv2,
#SearchMedRect,
#SearchLeaderBottomv2,
#ChanLeaderBottomv2,
#aus,
#adaptvBannerAd,
#adaptvBannerFrame,
#ad_holder,
#ChanHeader_holder,
#search_results li.sponsored_result{
	display: none !important;
}

/* OPACITY */
input[type=text],textarea,#share_api_buttons_dtabs {
	opacity: 0.3 !important;
}
input[type=text]:hover,textarea:hover,#share_api_buttons_dtabs:hover {
	opacity: 0.8 !important;
}


/* STYLE SETTINGS */
body {
	background: /*[[custombg]]*/ !important;
	color: #999 !important;
}
/* COLORS */
#fp_broadcast, #frontpage {
	background: transparent !important;
	color: #999 !important;
}
#footer a {
	color: #555 !important;
}
.wrapper .left_col,.wrapper .right_col,#directory .list_item .title, #page_wrapper, 

.static_left_col p {
	color: #888 !important;
}
#chat_lines, .chat_line, .videos_wrapper div.description, #footer, 

.dropmenu_links_container, .tooltip_content_container, #header_channels_search 

.form_container, #form_wrapper div, .home .callout_text {
	color: #999 !important;
}
#chat_lines li.fromjtv, .wrapper .left_col a, .wrapper .right_col 

a,.videos_wrapper,.videos_wrapper a, .tooltip_content_container .dir_follow 

a,.dropmenu_toggled .main {
	color: #CCC !important;
}
.wrapper .left_col .col_bg, .wrapper .dark, #wrapper,#data_tabs,#status, 

#description, #fans, #badges, #networks,#action_contents,#chat_container, 

#related,#directory,#footer,#help_us_translate a,#site_header {
	background: transparent !important;
}
#chat_lines, .wrapper .left_col .cntr, .wrapper .left_col .simple_chan_sect, 

#actions, #about, #stats, #data_tabs ul.tabs, #chat_container .chat_box, 

#related_list, #related .chan_sect_title, #directory .list_item {
	background: #111 !important;
}
#chat_lines li.fromjtv, #user_links p, .dropmenu_links_container, 

.tooltip_content_container, #header_channels_search .form_container, 

.dropmenu_toggled, #form_wrapper{
	background: #222 !important;
}
.tooltip_content_container .dir_follow a, #advanced_callout {
	background: #333 !important;
}
#search_results ul.search_list li.list_item  {
	background: #F1F1F1 !important;
}
#chat_lines, #status, #description, #fans, #badges, #networks,#action_links  {
	border: 0 !important;
}
#data_tabs ul.tabs, 

#chat_container,#stats,#actions,#about,#clips,#related,#user_links p,#directory 

.list_item,#form_wrapper {
	border: 1px solid #333 !important;
}
.tooltip_content_container .dir_follow a {
	border: 1px solid #555 !important;
}
.dropmenu_links_container,.tooltip_content_container,#header_channels_search 

.form_container {
	border: 1px solid #999 !important;
}
#search_results ul.search_list li.list_item {
	border: 1px solid #CCC !important;
}


/* BTNS */
.broadcast_btn {
	background: linear-gradient(to top, black, rgba(50,50,50,0.7)) !important;
        background: -webkit-linear-gradient(bottom, black, rgba(50, 50, 50, 0.7)) !important;
	border: 1px solid #444 !important;
	border-radius: 4px !important;
	line-height: 24px !important;
	margin-top: 12px !important;
	font-size: 12px !important;
	font-weight: bold !important;
	padding: 0 1em !important;
	box-shadow: 0 0 5px #000;
	text-shadow: 2px 2px 2px #000 !important;
}
.broadcast_btn:hover {
	background: linear-gradient(to top, rgba(180,20,20,0.7),rgba(255,80,80,0.7)) !important;
        background: -webkit-linear-gradient(bottom, rgba(180,20,20,0.7),rgba(255,80,80,0.7)) !important;
	text-decoration: none !important;
}
a.pretty_button {
    background: linear-gradient(to top, black, rgba(125,125,125,0)) !important;
    background: -webkit-linear-gradient(bottom, black, rgba(125,125,125,0)) !important;
    border: 1px solid #333 !important;
    box-shadow: 0 0 5px #000 !important;
    margin: 3px !important;
    text-shadow: 2px 2px 2px #000 !important;
    font-size: 1em !important;
}
a.pretty_button:hover {
    background: linear-gradient(to top, rgba(180,20,20,0.7),rgba(255,80,80,0.7)) !important;
    background: -webkit-linear-gradient(bottom, rgba(180,20,20,0.7),rgba(255,80,80,0.7)) !important;
}


/* FONT */
/* BE CAUTION WITH THE FONT-SIZE */
#status, #description, #fans, #badges, #networks, #stats h3 {
	font-family: Georgia, Helvetica, Arial;
}
#directory .list_item .title {
	font-size: 0.8em !important;
	font-weight: bold !important;
}
#directory .list_item .small, #footer {
	font-size: 0.7em !important;
}
#search_results .list_item .content p {
	font-size: 0.82em !important;
}
#stats h3 {
	font-size: 1em !important;
        white-space: normal !important;

}

/* END CUSTOM ZONE
*************************************************************/





/*************************************************************
* DO NOT EDIT */

.wrapper .left_col .info_pic,.wrapper .left_col .action_arrow,#banner_custom, 

#banner_default,#directory #right_col,#search_results .right_col {
	display: none !important;
}
.wrapper {
	width: 69.5em !important;
}
.wrapper .left_col {
	margin-right: 0.5em !important;
}
.wrapper .left_col .cntr,.wrapper .left_col 

.simple_chan_sect,#actions,#about,#stats,#data_tabs ul.tabs {
	margin: 5px 0 !important;
	padding: 2px 5px !important;
}
#data_tabs {
	margin: 0 !important;
	width: 100% !important;
}
#share_api_buttons_dtabs {
	bottom: 7px !important;
}
#stats h3 {
	margin: 4px 0 !important;
}
#chat_container {
	margin-top: 110px !important;
}
#directory .left_col {
	width: 50em !important;
}
#dir_filters {
	width: 45em !important;
}
#directory .list_item {
	text-align: center !important;
	padding: 5px !important;
}
#search_results ul.search_list li.list_item {
	padding: 5px !important;
	margin: 5px 0 !important;
}
#search_results .list_item .content{
	width: 32em !important;
}
#footer {
	border-top: 0 !important;
	margin: 0 auto !important;
}
#help_us_translate a {
	width: 100% !important;
}
}
