css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://cnbeta.com/"), url-prefix("http://www.cnbeta.com/") {
	#sideBar,#AdForward,#confident,#h_right,#logo center,#foot,#head{display:none;}
	#main{padding-top:50px !important;}
	#main #content{width:auto !important;}
	#ADcon0 .newslist{background:transparent !important}
	#active .newslist .desc{font-size:14px !important;}
	#active .newslist .desc b{display:none !important;}
	.newslist dl dt.topic {font-size:16px !important;}
}

@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {
	/* 隐藏不需要的元素 */
	#wrapper > *, #main > *, #newsBox> *, #news > *, #feedback > *, #news_content ins, .digbox, .gbox830 { display: none !important; }
	#main, #content, #news, #news_title, #news_author, #news_content, #post, #normal { display: block !important; }

	/* 去边框 */
	#contentWrapper, #news { border: none !important; }

	/* 黑底 */
	body { background: #000 !important; opacity: 0.9; }

	/* 白背景色 */
	#wrapper, #main { width: 95% !important; background: #fff !important; -moz-border-radius: 15px; -webkit-border-radius: 15px; }

	/* 边距 */
	#wrapper { padding: 10px 20px !important; margin: 20px auto !important; }

	/* 消除浮动 */
	#newsBox, #contentWrapper { float: none !important; width: 100% !important; }

	/* 标题字体大小 */
	#news_title { margin-bottom: 20px !important; font-size: 24px !important; }

	/* 增大内容面积 */
	#news_content { width: 100% !important; font-size: 18px !important; line-height: 1.5em !important; }
}
