/* AGENT_SHEET */
/*
 * Gmail Dark, Monospaced, Patched, Hovered
 * Author      : Julius B. Thyssen
 * Updated     : September 30, 2007
 * Usage       : Use with Stylish Firefox extension
(http://userstyles.org/) or copy to your Firefox userContent.css file
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.google.com/accounts/ServiceLogin?service=mail")
{
img[src$="talk_bubbles.gif"], .form-noindent[cellpadding="0"],
body br, td[width="75%"] {display: none!important;}
body, td {background-color: #efefee!important;}
table[width="95%"], table[width="94%"] {width: 40%!important;}
body > table:first-child {margin-bottom: -1em!important;}
}

@-moz-document url-prefix(https://mail.google.), url-prefix(http://mail.google.)
{
/* header row size / padding */
table.bookmarks { font-size:70%!important;background-color:
transparent!important;background-image: none!important; padding:6px 6px 15px 6px!important;}
.trb { font-size:70%!important;  padding-top:0px!important; }

/* overall styles */
.mb, .tb {/*font-family: 'Monospac821 BT','Courier
New',MonoSpace!important;*/ font-size:8pt!important;}
.mb * {font-size:8pt!important;}
.mb div {background-image: none!important;background-color:
transparent!important;border-width: 0px!important;}
.mb table, .mb tr, .mb td {background-color:
transparent!important;background-image: none!important;border-width:
0px;}
.tb { padding:0!important; }


/* Body backing */
.bn{background: #333 url("data:image/png;base64,")
top left repeat-x!important;
 color: #fdfdfc!important;font-family: Arial, Helvetica, sans-serif!important;}

/* Search Box */
[name="q"] {width:400px;
font-size:10pt!important;color:black!important;font-weight:bold!important;background:
#666666!important;}
#mt1 { padding-left:138px!important; }
#mt1 input { background: #666666!important; }

/* Hiding Bold Spam Count */
#ds_spam b { visibility: hidden; }
#ds_spam b::before { content: "Spam"; visibility: visible;
font-weight: normal; }

[id^=serd_] div {background-color: #333332!important;}

.dvm {
background:#AAA none repeat scroll 0%!important;
border-color:#fdfdfc!important;
border-style:solid!important;
}

.pu {
background:#AAA none repeat scroll 0%!important;
border-color:#fdfdfc!important;
border-style:solid!important;
}

.pu a:link{ color:#fdfdfc!important;}
.pu a:visited { color:#fdfdfc!important;}
.pu a:hover { color:#fdfdfc!important;background-color:
#cc7056!important;cursor:crosshair!important;}

.ma { border-width: 0px!important;}
.tbb {border-color: #777!important;background-color: #777!important;}

.ef {border-color: #444!important;border-width:
1px!important;border-style: solid!important;}
body { background-color:
#222!important;color:#fdfdfc!important;font-family: Arial, Helvetica,
sans-serif!important;}

[class~=nl] {color: #bababa!important;}

/* defaults for all links */
a:link{ color: #bbbbba!important;}
a:visited, .q, .gmail_quote{ color: #cccccc!important;}
a:hover { color: #eeeeed!important;cursor:crosshair!important;}

font, div, td { color:#fcfcfb!important;}

input *, textarea *, .aAutoComplete { background-color:
-moz-Field!important;color: -moz-fieldtext!important;}

span#more, #gbar .gbard, #gbar [class^="gb"] { background:
#000!important;border: none!important;}
#gbar .gbard a:hover, #gbar [class^="gb"] a:hover { background:
#2f2f2e!important;}
#gbi, #gbh { border: none!important;background: transparent!important;}

#guser { background:#000000!important; margin-bottom:5px!important;}
[id^="gbar"] { border: none!important;}
#gbar .gb1,#gbar .gb3,
#gbar .gbarp,
#guser {font-size:11px!important;}
#gbar .gbarp a,
#gbar .gbarp u,
#guser a,
#guser span {text-decoration:none!important;}
#gbarl {display:none!important;}
#prf_l {padding-top:8px!important;text-align:left!important;font-size:60%!important;}
#tt {padding-right:90px!important;}

/* No header image */
body div#ds_inbox img {display: none!important;}

div#nt1, td[vAlign="top"][width="149"] {display: none!important;}

/* Hiding GMail invites */
span[class~=ilc][class~=sxs] {display: none!important;}

/* Selected Colors */
[id^=prf_g]{color: #bbbbbb!important;}
.lk, .l, .lkw {color: #bbbbbb!important;}
.lk {text-decoration:none!important;}

/* quick contacts & labels box on the left */
.nb { border:2px solid #111!important; background-color:transparent!important;padding-bottom:3px!important; }
.nc { font-size:80%!important; }
#aca { visibility:hidden!important; display:none!important; }

/* unread mail messages */
[class~=ur] {background-color:
#111122!important;border-color:#fdfdfc!important;}

/* read mail messages */
[class~=rr] {background-color:
transparent!important;border-color:#fdfdfc!important;}
#pop table {border-color:#fdfdfc!important;}
#pop td, .s {background-color: #5f5f5f!important;}
.rr td {color:#bfbfbe!important;}

[id^="roster_filter"] { display: none!important;}  /* ,[id^="tcb"] */
