background-image:
url("data:image/png;base64,")!important;}
.cbuhd, .cbupd { background-color: transparent!important;
background-image:
url("data:image/png;base64,")!important;}

/* hovering message-list */
table.tlc tr.rr:hover,
table.tlc tr.y:hover { -moz-opacity: 0.7!important;background-color:
#181817!important;}
table.tlc tr.ur:hover { -moz-opacity: 1.0!important;background-color:
#111110!important;}

.s{ border-bottom: 0px!important;}

.pf { border-color: #999999!important;}
#ds_starred, .cbuhl, .cbuhm, .cbuhs, .cbul, .cbum, .cbus {
     display: none!important;}
[id^=cp_] {padding-right: 15px!important;margin-right:5px!important;}
#cp_r {padding-right: 5px!important;margin-right: 0px! important}
.pcl, .pfs, .pch, .pfs div{ border-width: 0px!important;}

table .pr, #pp, .pct, .pcl { background-color:
transparent!important;border-color: #777776!important;}

#hm ,td .pr{ background-color: #777777!important;border-color:
#777777!important;}
.ac span.asel{
background:#777 none repeat scroll 0%!important;border:
