.ctmec { background-color: #333332!important;}
.ctmes { background-color: #222221!important;}
.wackbody, .wackinfomsg, .wackinputopen, #bc_history, #wkbtm_ td,
.bz_cw_mom, .bz_cw_po {border-width: 0px!important;
background-color: #555554!important;}

/* Button styles */
button {border-color: #777!important;border-style:
solid!important;border-width: 2px!important;}
button div { color: black!important;}
.ab {  color: black!important;background-color:
#efefee!important;border-color: #777!important;border-style:
solid!important;border-width: 2px!important;}

/*Attempt at removing ads and footers*/
#rh, #rh .c{display:none!important;}
#fic{margin-right:inherit!important;}
div#rh {padding:0!important;width:auto!important;height:auto!important;float:right!important;}
div#fic {margin-right:0!important;}
#ft { display:none!important;}
div#ft {display:none!important;}

div#ap {overflow:hidden!important;}
div#ap .ar {float:left!important;margin-right:5px!important;}
div#nb_1 {display:none!important;}
div#tbd {min-height:170px!important;}
