css:
/* Torrid didit. */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('bahn.de') {

#angebote, .teaserbox, .tablist, .bar, .qf-teaser, .qf-infoteaser, .newsletter-box, #main-nav h2, #banner, .inner, #schnelleinstieg, #key-visual img{ display: none; }

#key-visual img{ height: 0; width: 0;}

#search input, #qf_form_bahn input, #qf_form_bahn select { 
        height: 17px;
        font-size: 1.2em;
        color: #666;
}
.button-inside, #country-select, #qf_form_bahn select {
        height: 20px;
}
#main-nav,#footer-nav { 
      background-color: #e08682;
}

#footer-nav { width: 100%; }

.qf-main { background: #fff; }
#footer { height: 3em; }

p.bold {text-align: center; font-weight: normal; color: #444; font-size: 1.1em; }

#key-visual {
z-index: -1;
background: url(
http://ais.badische-zeitung.de/piece/02/12/e7/a1/34793377.jpg
);
position: absolute;
background-repeat: no-repeat;
width: 809px;
height: 450px;
overflow: hidden;
margin-left: 170px;
}
}
