css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

.blog_menu .selected_blog #open_blog_link{color: #D0C9BF;}

div.l-header .selection_nipple{top:4px; border-bottom:12px solid rgba(135,115,95,0.5);}

body {
background: url('http://i52.tinypic.com/2yy78mr.jpg') bottom left no-repeat fixed,
url('http://i53.tinypic.com/2mo58ar.jpg') top left repeat fixed !important;
background-size: 100%;
}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #c2b08d !important;
}

ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #c2b08d !important;
}

ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #c2b08d !important;
}

ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #c2b08d !important;
}

ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #c2b08d !important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(135,115,95,0.5) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(135,115,95,0.5) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}

h1 {
color: #D3C7B3;
}

.no_posts_found, h1.dashboard_header {
color: #D3C7B3;
}

img#content_top, img#content_bottom {
display: none;
}

#right_column .controls_section li .add_and_remove, #right_column .controls_section li .customize, #right_column .controls_section li .mass_editor, #right_column .controls_section li .explore_more_tags {background-color:#8E7F68;}
#right_column .controls_section li .add_and_remove:hover, #right_column .controls_section li .customize:hover, #right_column .controls_section li .mass_editor:hover, #right_column .controls_section li .explore_more_tags:hover {background-color:#9B8D77;}
#right_column .controls_section .gradient {border-top: 0px;}

#posts .notification .avatar_frame{background-color:#675541;border:1px solid #675541;}
.first_notification .avatar_frame{border-top:1px solid #675541;border-right:1px solid #675541;border-left:1px solid #675541;}
.last_notification .avatar_frame{border-right:1px solid #675541;border-bottom:1px solid #675541;border-left:1px solid #675541;}
.single_notification .avatar_frame{border:1px solid #675541}

#posts li.notification.single_notification {border: transparent; background-color: rgba(142,127,104,0.8);}

#posts li.notification.alt {
border: transparent;
color: #675541;
background-color: rgba(142,127,104,0.8);
margin-bottom: 1px;
}

#posts .notification .hide_overflow a {
border-bottom: 1px solid #D0C9BF;
}

#posts li.notification.first_notification {
background-color: rgba(142,127,104,0.8);
color: #675541;
margin-bottom: 1px;
border: none;
}

#posts li.notification {
background-color: rgba(142,127,104,0.8);
border: transparent;
color: #675541;
margin-bottom: 1px;
}

#posts li.notification.last_notification {
border: transparent;
color: #675541;
}

ol#posts li.post .permalink {
background: rgba(129,129,129,0);
}

ol#posts li.post .post_avatar{
border-bottom: 0px solid #0A2939;
}

.answer_container {
background-color: #C2B08D;
border-bottom: 0px solid #B0976C;
color: #675541;
}

#auto_pagination_loader {
background: none;
border-top: 0px solid #1B2D42;
}

}

@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding: 40px 0;
}
}

