css:
@-moz-document domain(hotukdeals.com) {
  body, #container { background-image: url("http://imgcash3.imageshack.us/img171/6715/hukdbgcb4.gif") !important;  }
  body { background-color: #FFC4C7 !important; }

  #header > a[href="http://www.hotukdeals.com/"] {
    background: url("http://imgcash6.imageshack.us/img141/8594/hukdlogoblz6.gif") !important;
    display: block !important;
    float: left !important;
    width: 173px !important;
    height: 44px !important;
  }

  #header > a[href="http://www.hotukdeals.com/"] img { display: none !important; }

  .right_float_header p { color: #FFC4C7 !important; }
  #category_menu *.current, .forum_menu *.current { background: #EEE !important; }

  #footer { background: #B6000A !important; }
  #footer h2, #footer a, #footer dd, #footer dt, .footer_box { color: #FFF !important; }

  .footer_search, .deal_alerts {
    margin: 0 15px !important;
  }
}
