css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

#bar-deviant a{ color:#CC9999 !important; }
#bar-deviant{ color:#990000 !important; background-color: #660000 !important; border: 0px !important; }
.alt0{ background-color: #330000 !important; } 
.alt1{ background-color: #660000 !important; } 
.aside-left, .damncr .userinfo .avatar{ background-color: #000000 !important; border: 0px !important; } 
.damn-lo-row-top{ background-color: #000000 !important; border: 0px !important; } 
.damncr-infoctr { color: #CC3333 !important; border: 0px !important; border: 0px !important; } 
.damncr-infoctr a:hover{ color: #993333 !important; } 
.damncr-infoctr a{ color: #CC3333 !important; } 
.damncr-title a:hover{ color: #330000 !important; } 
.damncr-title a{ color: #990000 !important; } 
.damncr-title{ background-color: #660000 !important; color: #000000 !important; border: 0px !important; } 
.damncrc-chat .join, .damncrc-chat .part{ color: #FF6666 !important; } 
.damncrc-chat .other-hl{ background-color: #660000 !important; } 
.damncrc-chat .self-hl{ background-color: #330000 !important; } 
.damncrc-chat .userinfo-inner .avatar, .damncrc-chat .userinfo-inner .bodyarea{ background: none !important; background-color: #663333 !important; border: 1px !important; } 
.damncrc-chat .userinfo-inner div.read a:hover{ color: #996666 !important; } 
.damncrc-chat .userinfo-inner div.read a{ color: #CC9999 !important; } 
.damncrc-chat .userinfo-inner div.read{ color: #000000 !important; background-color: #993333 !important; border: 0px !important; } 
.damncrc-chat a:hover{ color: #996666 !important; } 
.damncrc-chat a{ color: #663333 !important; } .damncrc-chat span{ background: transparent !important; color:#FF0000 !important; border: 0px !important; } .damncrc-chat{ background: #000000 !important; color: #CC6666 !important; border: 0px !important; } 
.damncrc-chat-window { border:none !important; } 
.damncrc-error{ background-color: #993333 !important; color: #660000 !important; border: 0px !important; } 
.damncrc-topic a:hover{ color: #FF6666 !important; } 
.damncrc-topic a{ color: #CC3333 !important; } 
.damncrc-topic{ background-color: #330000 !important; color: #FF3333 !important; border: 0px !important; } 
.main span { color:#CC9999 !important; border: 0px !important; } 
.sidebar dl.simple dt { color:#660000 !important; border: 0px !important; } 
.tabbar a{ background-color: #660000 !important; border: 0px !important; } 
.tabbar a[style*=\"black\"]{ color: #CC9999 !important; background-color: #996666 !important; border: 0px !important; } 
.tabbar a[style*=\"maroon\"]{ background-color: #663333 !important; border: 0px !important; } 
.tabbar strong{ background: #CC0000 !important; border: 0px !important; } 
div#body { background-color: #000000 !important; border: 0px !important; } 
dl.simple dd, dl.flat dd { line-height:1.2em !important; color:#CC6666 !important; border: 0px !important; } 
input.button:hover{ color: #993333 !important; } 
input.button{ -moz-border-radius: 0 !important; background-color: #660000 !important; border: 0px !important; color: #FF6666 !important; font-weight: bold !important; font-size: 10px !important; text-transform: uppercase !important; padding: 3px !important; margin: 0px !important; }

}
