@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.youtube.com") {
body {
background:#3c3c3c !important;
color:#fff !important;
}
#unsubscribeDiv, .hid, .profileUnsubButton {
background-image:none !important;
}
.pagingDiv {
color:rgb(200,200,200) !important;
background:#3c3c3c !important;
}
.pagerCurrent {
color:#3c3c3c !important;
background:rgb(200,200,200) !important;
}
#masthead .nav-item .content {
background:#3c3c3c !important;
}
#channelVidsDiv {
background:#3c3c3c !important;
}
#subscribeDiv, #subscribeDiv b,#subscribeDiv a {
background-image:none !important;
}
.subButton b, .profileUnsubButton b {
background-image:none !important;
}
.smallLabel, .label, #vidTitle {
color:rgb(200,200,200) !important;
}
#masthead .upload-button .uploadtext {
color:rgb(200,200,200) !important;
background-image:none !important;
}
a,.smallText a, .elink, .vSnippetTitle a, .info-section a {
color:rgb(200,200,200) !important;
text-decoration:none !important;
}
a:hover,.smallText a:hover, .elink, .vSnippetTitle a:hover , .info-section a:hover {
color:#fff !important;
text-decoration:none !important;
}
#masthead #search-form, .search-bar {
background:#3c3c3c !important;
}
input[type=text] {
color:rgb(200,200,200) !important;
background:#3c3c3c;
border:1px solid rgb(200,200,200) !important;
}
input[type=submit] {
color:rgb(200,200,200) !important;
background:#3c3c3c !important;
border:1px solid rgb(200,200,200) !important;
}
#masthead .logo {
background-image: url(data:image/png;base64,%3D) !important;
width:132px !important;
height:46px !important;
}
#masthead .logo img {
width: 0 !important;
}
#masthead .nav-item .leftcap, #masthead .nav-item .rightcap, #subscribeDiv, #unsubscribeDiv, #channelBrandDiv, #hpSideContent, #masthead .bar .leftcap, #masthead .bar .leftcap, #masthead .bar .rightcap, .hpYellowBlock, .hpPurpleBlock, #hpMainContent, #footer .search, #footer .links, #copyright, .hpYellowBlockBot, .hpPurpleBlockBot, .padT10,#footer, .contentBox, #left-column, #header-left, #body-column, #header-middle, #header-wrapper, #sideContentWithPVA, #searchSectionHeader .name, #searchSectionHeader, #search-options-container, .vInfo, #vResponseDiv, #recent_comments, #commentsDiv, #someStats, #actionsAreaDiv .actionLinks, #ratingAndStatsDiv, .wsHeading, .wsBody, .marT5, #relatedVidsToggle, .expand-container .expand-header {
display:none !important;
}
}
