css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("avaxhome.ws") {
.twitter{
display:none;
}
center {
display:none;
}
div[align="left"]{
display:none;
}
.banner{
display: none;
border: 0px;
}
.container{
overflow:auto;
}
.panel-wrap > #main-info-container > .news[author_id]{
display:block;
float:none;
width:100%;
height:100%;
}
.panel-wrap > #main-info-container > .news{
float: left;
width:170px;
height: 300px;
overflow: hidden;
border: 1px solid black;
position:relative;
padding:15px;
}
.panel-wrap > #main-info-container > .news .title, .info, .text{ font-size: 9px !important;}
.panel-wrap > #main-info-container > .news .actions{
position:absolute;
top: 0px;
}
.panel-wrap > #main-info-container > .news[author_id] .title, .info, .text{ font-size: 12px;}

.panel-wrap > #main-info-container > .news .actions a{
width: 170px;
height: 300px;
display: block ;
}

.panel-wrap > #main-info-container > .news img{
width: 140px;
}
.pagination{
position:absolute;
top: 45px;
background:white;
}
.hr{
display:none;
}
#main-info-container > br{display:none;}


#content > #main-info-container > .news .text > .image:first-child{
float:left;
margin-right: 10px;
}

#content > #main-info-container > .news .text > .image:first-child  img {max-width:250px;}
#main-categories + .container{
border:none !important;
}
#main-categories + .container + .container{
display:none;
}
#main-info-container + .container, #apBreakEnd + .container{
border:none !important;
display:none !important;
}
div#main-categories ul.external-links{
display:none;
}
.file-express{display:none;}
br + .image > a[rel="nofollow"] > img{max-width:200px;
float:left;
}
br + .image + br + b, br + .image + br + .center > b, br + .image + .center, br + .center + br + .center > b, br + .image + br + .spoiler {
clear:both;
display:block;
}
.image + br{display:none;}
}
