css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.stern.de") {


/* ---------------------------------------------------------------- */
/* Sachen entfernen/verstecken: */
/* ---------------------------------------------------------------- */

div#adRahmen,         /* Werbung                       */
div#banner,           /* Werbung                       */
div#contentAd,        /* Werbung                       */
div#contenttextlink,  /* Werbung                       */
div#sky,              /* Werbung (Hintergrund, etc.)   */
div.imageRelative, /* Verwandte Themen                 */
div.boxGeneral,    /* Box "Mehr zum Thema"             */
div#social,        /* social bookmarks                 */
div#suchForm,      /* Suchformular                     */
table.marktplatz   /* "Marktplatz & Service-Angebote"  */
{ 
  display: none !important;
}


div#contRight    /* rechte Leiste */
{
  display: none !important;
  margin: 0 !important;
  width: 1% !important;
}



/* ---------------------------------------------------------------- */
/* ZENTRIEREN: */
/* ---------------------------------------------------------------- */

div.zwischenleiste 
/* Zwischenleisten (Bilder, related Stuff) in die Mitte */
{
  /* padding: 0px !important; 
  margin: 0px auto !important; */
  margin-left: auto !important; 
  margin-right: auto !important;
  text-align: center !important;
}

div.artikel,        /* Artikel in die Mitte */
h1.center+div,      /* "Der Tag in Bildern" in die Mitte */
table[width="500"]  /* "Der Tag in Bildern" in die Mitte */
{ 
  margin-left:  auto !important;
  margin-right: auto !important;

}

div#rahmen,
div#contentContainer
{
  width: 90% !important;
  margin-left: auto !important; 
  margin-right: auto!important;
}


/* ---------------------------------------------------------------- */
/* DIVERSES */
/* ---------------------------------------------------------------- */

/* WeitenbeschrÃ¤nkungen entfernen */
#TopTeaser,
#TopContent,
#navTop,
#navSub,
#navSubRahmen,
#contentContainer,
#mainTeaser,
#L3NavExtra,
.alleExtras,
#contRight h2,
.sidebarTeaserKasten,
.erlesen,
.artikelBlock,
#navFooter,
.leftTeaser,
.mainTeaser
{
  width: auto !important;
}


div#TopContent
{
  float: none !important;
}

div#TopContent .next,
div.leftTeaser .next
{
  clear: none !important;
}


div#contMain
{
  width: 90% !important;
  padding: 10px 10px !important;
  margin: 10px !important; 
}


div#navFooter,
div#navFooter .grau
{
  font-size: 0.8em !important;
}


div.mainTeaser
{
  clear: left !important;
}

}
