css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freekick.org") {

label.alternateRow, div.alternateRow, ul.alternateRow, li.alternateRow, a.alternateRow {
border: 0px !important;
}

tr.alternateRow th, tr.alternateRow td {
    border-width: 0px 0 !important;
}

label.alternateEven, div.alternateEven, li.alternateEven, ul.alternateEven, a.alternateEven, tr.alternateEven td {
 background: #FFFFFF !important;
}

label.alternateEvenMarked,
div.alternateEvenMarked,
ul.alternateEvenMarked,
li.alternateEvenMarked,
a.alternateEvenMarked,
tr.alternateEvenMarked td { 
  background: #9bcceb !important; 
}

label.alternateEvenMarked:hover,
div.alternateEvenMarked:hover,
ul.alternateEvenMarked:hover,
li.alternateEvenMarked:hover,
a.alternateEvenMarked:hover,
tr.alternateEvenMarked:hover td {
  background: #87b8d7 !important; 
}

label.alternateRow:hover,
div.alternateRow:hover,
ul.alternateRow:hover,
li.alternateRow:hover,
a.alternateRow:hover,
tr.alternateRow:hover td { 
  background: #d2d2d2 !important; 
}

.information-grey {
    border-width: 0px 0 !important;
    background: #ffffff !important;
}

}
