@-moz-document domain("neopets.com") {

/********************************************************************/
/* HTML Tag Styles **************************************************/
/********************************************************************/

	A:Link, A:Visited {
		color: #9C7D35 !important;
	}

	A:Hover {
		color: #C4A55E !important;
	}

/********************************************************************/
/* Foundation IDs ***************************************************/
/********************************************************************/

	#main {
		border-left: 2px solid #747474 !important;
		border-right: 2px solid #747474 !important;
	}

	#header {
		background-color: #7D7664 !important;
		background-image: url("http://images.neopets.com/themes/009_qas_93707/header_bg.png") !important;
                border: 0px !important;
	}

	#footer {
		background-color: #7D7664 !important;
		background-image: url("http://images.neopets.com/themes/009_qas_93707/footer_bg.png") !important;
	}

        #ban {
                border: 0px !important;
        }


/********************************************************************/
/* Header Stuff *****************************************************/
/********************************************************************/

/* Navigation.  Duh.  */
	ul.dropdown {
		border: 1px solid #635949 !important;
		background-color: #E0C05C !important;
	}

	LI:hover UL.dropdown A, LI.over UL.dropdown A {
		color: #000000 !important;
	}

	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {
		color: #635949 !important;
	}

/* Event Icons */
	.eventIcon, .user {
		color: #FFFFFF !important;
	}

	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,
	.user A:Link, .user A:Hover, .user A:Visited {
		color: #C4A55E !important;
	}

/* Clock */
	#nst {
		color: #000000 !important;
	}


/********************************************************************/
/* Sidebar Classes **************************************************/
/********************************************************************/

	.sidebarModule {
		border: 2px solid #E4E4E4 !important;
	}

	.sidebarTable {
		border: 2px solid #C9C9C9 !important;
	}

	.sidebarHeader {
		background-color: #8A6359 !important;
		border-bottom: 2px solid #C9C9C9 !important;
	}

	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {
		color: #000000 !important;
	}


/********************************************************************/
/* Content Classes **************************************************/
/********************************************************************/

/* We need a few styles to make a module work */
	.contentModuleHeader {
		background-color: #000000 !important;
		color: #FFFFFF !important;
	}

	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {
		color: #C5C5C5 !important;
	}

	.moreLink {
		border: 1px solid #8F8F8F !important;
	}

	.contentModuleHeaderAlt {
		background-color: #635949 !important;
		color: #FFFFFF !important;
	}

	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {
		color: #D2CCC4 !important;
	}

	.moreLinkAlt {
		border: 1px solid #988974 !important;
	}


/********************************************************************/
/* Footer Classes ***************************************************/
/********************************************************************/

	.footerForm {
		color: #7D7664 !important;
	}

	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {
		color: #1B1B1B !important;
	}


/********************************************************************/
/* Misc Classes of Doom *********************************************/
/********************************************************************/

/* Ad banners.  No comment */
	#ban {
		background-color: #7D7664 !important;
		background-image: url("http://images.neopets.com/themes/009_qas_93707/banner_bg.png") !important;
	}

	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
		background-color: #C4A55E !important;
		color: #FFFFFF !important;
	}

	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
		background-color: #8C6F31 !important;
		color: #FFFFFF !important;
	}

}
